	/*///------------------------------------------------------------------------
			>>> INITIALIZE CLASS
	/*///------------------------------------------------------------------------
	FINAL PUBLIC FUNCTION __CONSTRUCT()
	{
		// STORE THE DEFAULT VALUES FOR VARIABLES
		$this->ORIGINALS = $this->VARIABLES;
		
		// PREPARE AN ANONYMOUS FUNCTION
		SELF::$ANON_METHOD = function ()
		{
			$args = func_get_args();
			
			if ( func_num_args() == 1 )
			{
				return $args[0];
			}
			
			return $args;
		};
		
		// PREPARE AN ANONYMOUS FUNCTION FOR FILES
		SELF::$HANDLE_FILE = function ( $data )
		{
			// GET THE INFORMATION REQUESTED
			$contentType = ( new finfo ( FILEINFO_MIME_TYPE ) )->buffer ( $data );
			if ( stripos ( $contentType, 'text' ) === false )
			{
				// CREATE FILENAME
				$filename = time().rand(100000,999999);
				
				//SET THE HEADERS
				header ("Content-Type: {$contentType}");
				header ("Content-Disposition: FileName=\"{$filename}\"");
				
				// ECHO THE DATA
				echo $data;
				
				// TERMINATE THE SCRIPT
				die;
			}
			
			// ECHO AND RETURN THE DATA BY DEFAULT
			echo $data;
			return $data;
		};
		
		// PREPARE VARIABLE FOR VALIDATION
		$connectible = false;
		$arguments = func_get_args();
		
		if
		(
			// IF THE NUMBER OF ARGUMENTS IS NOT 0 AND...
				func_num_args() > 0 &&
				
			// IF THE METHOD FAILS
				(
					(
						$connectible =
						(
							func_num_args() == 4 &&
							$this->VARIABLES['CONNECTION'] === NULL &&
							SELF::ARRAY_IS_TYPE_OF ( $arguments, 'string' ) &&
							!method_exists ( __CLASS__, func_num_args()[0] )
						)
					) ||
					(
						SELF::ACCEPTED ( $arguments, 'BIND', __CLASS__ ) &&
						( $result = $this->BIND ( ...$arguments ) ) == SELF::FAIL
					)
				)
		)
		{
			if ( $connectible )
			{
				// CONNECT TO DATABASE
				$this->PDO_CONNECT( ...$arguments );
			}
			else if ( SELF::$FAIL )
			{
				// PREPARE A BUG TRACE
				SELF::BUG_TRACE ( SELF::FAIL );
			}
		}
	}
	
	/*///------------------------------------------------------------------------
			>>> PERFORM CORE METHODS DURING INVOKE
	/*///------------------------------------------------------------------------
	FINAL PUBLIC FUNCTION __INVOKE()
	{
		// PREPARE THE RESULT
		$result = null;
		
		// PREPARE VARIABLE FOR VALIDATION
		$connectible = false;
		$arguments = func_get_args();
		
		if
		(
			// IF THE NUMBER OF ARGUMENTS IS NOT 0 AND...
				func_num_args() > 0 &&
				
			// IF THE METHOD FAILS
				(
					(
						$connectible =
						(
							func_num_args() == 4 &&
							$this->VARIABLES['CONNECTION'] === NULL &&
							SELF::ARRAY_IS_TYPE_OF ( $arguments, 'string' ) &&
							!method_exists ( __CLASS__, func_num_args()[0] )
						)
					) ||
					(
						SELF::ACCEPTED ( $arguments, 'BIND', __CLASS__ ) &&
						( $result = $this->BIND ( ...$arguments ) ) == SELF::FAIL
					)
				)
		)
		{
			if ( $connectible )
			{
				$result = $this->PDO_CONNECT( ...$arguments );
			}
			else if ( SELF::$FAIL )
			{
				// PREPARE A BUG TRACE
				SELF::BUG_TRACE ( SELF::FAIL );
			}
		}
		
		// RETURN THE RESULT IF SUCCESSFUL
		return $result;
	}
	
	/*///------------------------------------------------------------------------
			>>> THROW ERROR ON UNEXISTING METHOD
	/*///------------------------------------------------------------------------
	FINAL PUBLIC FUNCTION __CALL
	(
			$method_name,	// NAME OF METHOD BEING CALLED
			$arguments		// ARGUMENTS PASSED
	)
	{
		// PREPARE VARIABLES
		$method_name	= strtoupper ( $method_name );
		$isStatic		= false;
		$isFinal		= false;
		
		// CHECK IF...
		if
		(
			// THE METHOD NAME IS AN ALIAS OF ANOTHER METHOD AND...
				array_key_exists ( $method_name, SELF::$ALIAS ) &&
				
			// THE METHOD EXISTS AND...
				method_exists ( __CLASS__, SELF::$ALIAS[$method_name] ) &&
				
			// THE METHOD IS PUBLIC AND...
				SELF::METHOD_IS_PUBLIC ( SELF::$ALIAS[$method_name], $isStatic, $isFinal ) &&
				
			// THE ARGUMENTS ARE ACCEPTED BY THE METHOD
				SELF::ACCEPTED ( $arguments, SELF::$ALIAS[$method_name], __CLASS__ )
		)
		{
			// CALL METHOD
			return call_user_func_array ( array ( __CLASS__, SELF::$ALIAS[$method_name] ), $arguments );
		}
		
		// THROW AN ERROR IF KEY DOES NOT EXIST
		SELF::BUG_TRACE ( "The method <b>$method_name()</b> cannot be found." );
	}
	
	/*///------------------------------------------------------------------------
			>>> THROW ERROR ON UNEXISTING METHOD OR RETURN SELF::$STATIC
	/*///------------------------------------------------------------------------
	FINAL PUBLIC STATIC FUNCTION __CALLSTATIC
	(
			$method_name,	// NAME OF METHOD BEING CALLED
			$arguments		// ARGUMENTS PASSED
	)
	{
		// PREPARE VARIABLES
		$method_name	= strtoupper ( $method_name );
		$isStatic		= false;
		$isFinal		= false;
		
		// CHECK IF...
		if
		(
			// THE METHOD NAME IS AN ALIAS OF ANOTHER METHOD AND...
				array_key_exists ( $method_name, SELF::$ALIAS ) &&
				
			// THE METHOD EXISTS AND...
				method_exists ( __CLASS__, SELF::$ALIAS[$method_name] ) &&
				
			// THE METHOD IS PUBLIC AND...
				SELF::METHOD_IS_PUBLIC ( SELF::$ALIAS[$method_name], $isStatic, $isFinal ) &&
			
			// THE METHOD IS STATIC AND...
				$isStatic &&
				
			// THE ARGUMENTS ARE ACCEPTED BY THE METHOD
				SELF::ACCEPTED ( $arguments, SELF::$ALIAS[$method_name], __CLASS__ )
		)
		{
			// CALL METHOD
			return call_user_func_array ( array ( __CLASS__, SELF::$ALIAS[$method_name] ), $arguments );
		}
		else if
		(
			// NAME EXISTS AS KEY OF STATIC PARAMETERS AND...
				array_key_exists ( $method_name, SELF::$STATIC ) &&
				
			// THE NUMBER OF ARGUMENTS IS 0
				count ( $arguments ) == 0
		)
		{
			// RETURN THE VALUE OF STATIC PARAMETERS
			return SELF::$STATIC [ $method_name ];
		}
		
		// THROW AN ERROR IF KEY DOES NOT EXIST
		SELF::BUG_TRACE ( "The method <b>$method_name()</b> cannot be found." );
	}
	
	/*///------------------------------------------------------------------------
			>>> CHECK IF ELEMENT EXISTS IN $this->VARIABLES
	/*///------------------------------------------------------------------------
	FINAL PUBLIC FUNCTION __ISSET
	(
			string $array_key // ARRAY INDEX OR KEY
	): BOOL
	{
		// CAPITALIZE FOR CASE INSENSITIVITY
		$array_key = strtoupper ( $array_key );
		
		// CHECK IF ELEMENT EXISTS
		return array_key_exists ( $array_key, $this->VARIABLES );
	}
	
	/*///------------------------------------------------------------------------
			>>> CHANGE DATA OF ELEMENT FROM $this->VARIABLES IF ELEMENT EXISTS
	/*///------------------------------------------------------------------------
	FINAL PUBLIC FUNCTION __SET
	(
			string $array_key,	// ARRAY INDEX OR KEY
			$value				// VALUE TO BE STORED
	)
	{
		// CAPITALIZE FOR CASE INSENSITIVITY
		$array_key = strtoupper ( $array_key );
		
		if
		(
			// ASSIGN VALUE ONLY IF ELEMENT EXISTS AND...
				array_key_exists ( $array_key, $this->VARIABLES ) &&
				(
					// IF THE VALUE IS A RESOURCE OR...
						(
							$array_key == 'CONNECTION' &&
							(
								stripos ( gettype ( $value ) , 'resource' ) !== false ||
								$value === NULL
							)
						) ||
					// IF THE VALUE IS A STRING OR...
						(
							is_string ( $value ) &&
							(
								$array_key == 'HOST' 			||
								$array_key == 'USERNAME' 		||
								$array_key == 'PASSWORD' 		||
								$array_key == 'DATABASE' 		||
								(
									$array_key == 'DEFAULT_OPERATOR' &&
									(
										( $value = strtoupper ( $value ) ) == 'OR' ||
										( $value = strtoupper ( $value ) ) == 'AND'
									)
								)
							)
						) ||
					// IF THE VALUE IS AN ARRAY
						(
							is_array ( $value )									&&
							( $array_key == 'SKIP' || $array_key == 'IGNORE' )	&&
							SELF::ARRAY_IS_TYPE_OF ( $value, 'string' )
						)
				)
		)
		{
			$this->VARIABLES [ $array_key ] = $value;
		}
	}
	
	/*///------------------------------------------------------------------------
			>>> CHANGE DATA OF ELEMENT FROM $this->VARIABLES TO DEFAULT
	/*///------------------------------------------------------------------------
	FINAL PUBLIC FUNCTION __UNSET
	(
			string $array_key // ARRAY INDEX OR KEY
	): BOOL
	{
		// CAPITALIZE FOR CASE INSENSITIVITY
		$array_key = strtoupper ( $array_key );
		
		// ASSIGN VALUE ONLY IF ELEMENT EXISTS
		if ( array_key_exists ( $array_key, $this->VARIABLES ) )
		{
			$this->VARIABLES [ $array_key ] = $this->ORIGINALS [ $array_key ];
			return true;
		}
		
		// RETURN FALSE IF NOT FOUND
		return false;
	}
	
	/*///------------------------------------------------------------------------
			>>> GET ELEMENT FROM $this->VARIABLES OR FROM
				$this->TASK_VARS IF ELEMENT EXISTS
	/*///------------------------------------------------------------------------
	FINAL PUBLIC FUNCTION __GET
	(
			string $variable_called // ARRAY INDEX OR KEY
	)
	{
		if
		(
			// IF THE VARIABLE BEING CALLED EXISTS AND...
				array_key_exists ( $variable_called, $this->TASK_VARS ) &&
				
			// IF THE CLASS ACCESSING THE VARIABLE IS THIS CLASS
				( ( count ( $trace = debug_backtrace() ) - 1 ) > 0 ) &&
				array_key_exists ( 'class', $trace [ 1 ] ) &&
				__class__ == $trace [ 1 ] [ 'class' ]
		)
		{
			// RETURN THE ARRAY ELEMENT
			return $this->TASK_VARS [ $variable_called ];
		}
		else if
		(
			// IF THE VARIABLE CALLED EXISTS
				array_key_exists ( ( $KEY = strtoupper ( $variable_called ) ), $this->VARIABLES )
		)
		{
			// RETURN THE ARRAY ELEMENT
			return $this->VARIABLES [ $KEY ];
		}
		
		// RETURN NULL IF NONE FOUND
		return null;
	}
	
	
	/*///------------------------------------------------------------------------
