	/*///------------------------------------------------------------------------
			>>> CHECK IF URL EXISTS
	/*///------------------------------------------------------------------------
	FINAL PRIVATE STATIC FUNCTION URLEXISTS
	(
			string $url	// URL TO VALIDATE
	)
	{
		return strpos(@get_headers($url)[0],'200') === false ? false : true;
	}
	
	/*///------------------------------------------------------------------------
			>>> CHECK IF METHOD IS PUBLIC
	/*///------------------------------------------------------------------------
	FINAL PRIVATE STATIC FUNCTION METHOD_IS_PUBLIC
	(
			string $method_name,		// NAME OF METHOD BEING CALLED
			bool &$isStatic = false,	// OPTIONAL RETURN FOR CHECKING IF METHOD IS STATIC
			bool &$isFinal = false		// OPTIONAL RETURN FOR CHECKING IF METHOD IS FINAL
	): BOOL
	{
		// CHECK IF METHOD EXISTS
		if ( method_exists ( __CLASS__, $method_name ) )
		{
			// CHECK IF METHOD IS PUBLIC, STATIC, AND FINAL
			$method = new ReflectionMethod ( __CLASS__, $method_name );
			$isStatic = $method->isStatic();
			$isFinal = $method->isFinal();
			if ( $method->isPublic() ) { return true; }
		}
		
		// RETURN FALSE IF METHOD IS NOT ACCESSIBLE
		return false;
	}
	
	/*///------------------------------------------------------------------------
			>>> CHECK IF METHOD IS STATIC RETURN
				FALSE IF METHOD IS IN OBJECT CONTEXT
	/*///------------------------------------------------------------------------
	FINAL PRIVATE STATIC FUNCTION I_AM_STATIC()
	{
		return
		(
			// IF THE CLASS ACCESSING THE VARIABLE IS THIS CLASS AND...
				( ( $reference = count ( $trace = debug_backtrace() ) - 1 ) > 0 ) &&
				array_key_exists ( 'class', $trace [ $reference ] ) &&
				__class__ == $trace [ $reference ] [ 'class' ] &&
				
			// IF THE METHOD ACCESSING THIS VARIABLE IS STATIC
				array_key_exists ( 'type', $trace [ $reference ] ) &&
				'::' == $trace [ $reference ] [ 'type' ]
		);
	}
	
	/*///------------------------------------------------------------------------
			>>> DETERMINE THE TYPE OF VARIABLE
	/*///------------------------------------------------------------------------
	FINAL PUBLIC STATIC FUNCTION VAR_TYPE( $VAR ): STRING
	{
		if ( is_null($VAR) )
		{
			return 'NULL';
		}
		else if ( is_object($VAR) && is_callable($VAR) && ( $VAR instanceof Closure ) )
		{
			return 'CLOSURE';
		}
		else if ( is_int ( $VAR ) || ( is_numeric ( $VAR ) && (int)$VAR == (float)$VAR ) )
		{
			return 'INTEGER';
		}
		else if ( is_float ( $VAR ) || is_numeric ( $VAR ) )
		{
			return 'DOUBLE';
		}
		else if ( is_string ( $VAR ) )
		{
			return 'STRING';
		}
		else if ( is_bool ( $VAR ) )
		{
			return 'BOOLEAN';
		}
		else if ( is_array ( $VAR ) )
		{
			return 'ARRAY';
		}
		else if ( is_object ( $VAR ) )
		{
			return 'OBJECT';
		}
		else if ( is_resource ( $VAR ) )
		{
			return 'RESOURCE';
		}
		else if ( gettype ( $VAR ) == 'resource (closed)' )
		{
			return 'RESOURCE (CLOSED)';
		}
		else if ( gettype ( $VAR ) == 'unknown type' )
		{
			return 'UNKNOWN TYPE';
		}
		
		return strtoupper ( gettype ( $VAR ) );
	}
	
	/*///------------------------------------------------------------------------
			>>> CHECK IF VARIABLE/VARIABLES IS/ARE EMPTY
	/*///------------------------------------------------------------------------
	FINAL PUBLIC STATIC FUNCTION IS_EMPTY(): BOOL
	{
		if ( func_num_args() === 0 ) return true;
		
		foreach ( func_get_args() as $arg )
		{
			if ( empty ( $arg ) ) return true;
		}
		
		return false;
	}
	
	/*///------------------------------------------------------------------------
			>>> CHECK IF THE ARRAY IS AN ARRAY OF EXPECTED TYPE
	/*///------------------------------------------------------------------------
	FINAL PUBLIC STATIC FUNCTION ARRAY_IS_TYPE_OF
	(
			array $array,	// ARRAY TO BE CHECKED
			string $type	// VARIABLE TYPE EXPECTED
	): BOOL
	{
		foreach ( $array as $value )
		{
			if ( stripos ( gettype ( $value ), $type ) === false )
			{
				return false;
			}
		}
		
		return true;
	}
	
	/*///------------------------------------------------------------------------
			>>> CHECK MANDATORY ELEMENTS IN ARRAY
				array $specimen_array,			// THE ARRAY TO BE CHECKED
				int $requried_size,				// REQUIRED SIZE
				string $definedAsEmpty = NULL	// DEFINE WHAT IS CONSIDERED EMPTY
	/*///------------------------------------------------------------------------
	FINAL PUBLIC STATIC FUNCTION MANDATORY_ARRAY_ELEMENTS(): BOOL
	{
		$me = func_get_args();
		$types = 'array, int, string';
		$names = 'specimen_array, requried_size, definedAsEmpty';
		$default_values = [NULL];
		if ( !SELF::ARGS( $me, $types, $default_values, $names ) ) return SELF::FAIL;
		
		
		// RETURN FALSE IF THE ARRAY DOES NOT MEET THE REQUIRED SIZE
		if ( count ( $me->specimen_array ) < $me->requried_size ) { return false; }
		
		// START COUNTING
		$ctr = 1;
		
		// CHECK IF EACH REQUIRED ELEMENT IS EMPTY
		foreach ( $me->specimen_array as $element )
		{
			if ( $element === $me->definedAsEmpty )
			{
				return false;
			}
			
			if ( $ctr >= $me->requried_size ) { break; }
			$ctr++;
		}
		
		// RETURN TRUE IF THE ARRAY PASSED THE TEST
		return true;
	}
	
	/*///------------------------------------------------------------------------
			>>> CHECK IF THE LIST OF ARRAY KEYS EXIST WITHIN ARRAY
	/*///------------------------------------------------------------------------
	FINAL PUBLIC STATIC FUNCTION ARRAY_KEYS_EXIST
	(
			array $array,		// ARRAY TO BE PROCESSED
			string $array_keys	// COMMA SEPARATED ARRAY INDICES OR KEYS
	): BOOL
	{
		// BEGIN READING THROUGH THE LIST
		foreach ( explode( ',', str_replace ( ' ', '', $array_keys ) ) as $key )
		{
			// CHECK IF KEY EXISTS
			if ( !array_key_exists ( $key, $array ) )
			{
				// IMMEDIATELY RETURN FALSE IF ONE OF THE KEYS DOESN'T EXIST
				return false;
				break;
			}
		}
		
		// RETURN TRUE IF THE TEST IS SUCCESSFUL
		return true;
	}
	
	/*///------------------------------------------------------------------------
			>>> CHECK IF ONE OR MORE OF THE LISTED ARRAY KEYS EXIST WITHIN ARRAY
	/*///------------------------------------------------------------------------
	FINAL PUBLIC STATIC FUNCTION SOME_ARRAY_KEYS_EXIST
	(
			array $array,		// ARRAY TO BE PROCESSED
			string $array_keys	// COMMA SEPARATED ARRAY INDICES OR KEYS
	): BOOL
	{
		// BEGIN READING THROUGH THE LIST
		foreach ( explode( ',', str_replace ( ' ', '', $array_keys ) ) as $key )
		{
			// CHECK IF KEY EXISTS
			if ( array_key_exists ( $key, $array ) )
			{
				// IMMEDIATELY RETURN TRUE IF ONE OF THE KEYS IS FOUND
				return true;
				break;
			}
		}
		
		// RETURN FALSE IF NONE OF THE KEYS ARE FOUND
		return false;
	}
	
	
