	/*///------------------------------------------------------------------------
	
	
	/*///------------------------------------------------------------------------
			>>> BIND POST/REQUEST/GET REQUEST KEYS ($_REQUEST['KEYS']) TO METHOD
			SYNTAX:
			$this->BIND
			(
				'method_name',		// NAME OF METHOD
				new class,			// INSTANCE OF CLASS
				'key1, key2',		// ARRAY KEYS OF $_REQUEST TO BE BOUND
				'GET',				// REQUEST TYPE
				[$my, $additions],	// ADDITIONAL PARAMETERS FOR METHOD
				true,				// PRIORITIZE THE ADDITIONAL PARAMETERS
				1,					// MODE FOR REQUEST
				function(){ do(); }	// ANONYMOUS FUNCTION FOR PROCESSING RESULTS
			)
			false:	$return = $this->method_name($_GET['key1'], $_GET['key2'], $my, $additions)
			true:	$return = $this->method_name($my, $additions, $_GET['key1'], $_GET['key2'])
			function($return) { what_to_do_with($return); }
			MODE:
				(1) - all data from $_REQUEST are passed as parameters to method
					- the method will execute even if the keys are not in $_REQUEST
				(2) - data from $_REQUEST are not used as parameters to method
					- the method will execute even if the keys are not in $_REQUEST
				(3) - all data from $_REQUEST are passed as parameters to method
					- the method will not execute if the keys are not in $_REQUEST
				(4) - data from $_REQUEST are not used as parameters to method
					- the method will not execute if the keys are not in $_REQUEST
			SAMPLE USE:
				$tsk = new TASK('pdo_connect', ['usr', 'pwd', 'localhost', 'db'], true);
				$tsk->skip = ['request'];
				$query =	"SELECT data, CONCAT_WS ( '.', name, extension ) AS filename ".
							"FROM file WHERE ( id = :id AND id LIKE :id ) LIMIT 1;";
				$function = function($result)
				{
					TASK::SET_MIMETYPE($result[0]['data'], $result[0]['filename']);
					echo $result[0]['data'];
					die;
				};
				$tsk('PDO_QUERY_RESULT', 'id, filename', [$query], 2, $function);
	/*///------------------------------------------------------------------------
	FINAL PUBLIC FUNCTION BIND()
	{
		// 0. PROVIDE AN OVERLOADING-LIKE FEATURE TO THE METHOD
		$me = func_get_args();
		$types = 'string, object, string, string, array, bool, int, closure';
		$default_values = [ null, '', 'REQUEST', [], false, 0, null ];
		$names =	'method_name, class_instance, request_keys, request, ' .
					'declarations, declarations_first, mode, action';
		if ( !SELF::ARGS( $me, $types, $default_values, $names ) ) return SELF::FAIL;
		
		// 1. PREPARE THE VARIABLES
		$PARTS				= SELF::DISASSEMBLE ( $me->method_name );
		$class_instance		= $me->class_instance;
		$context			=	(	strpos ( $PARTS [ 0 ], ( $context = '::' ) )
								||	strpos ( $PARTS [ 0 ], ( $context = '->' ) )
								)?	$context : '';
		$method_class		=	( $context == '' ) ?
								( ( $class_instance !== null ) ? get_class ( $class_instance ) : __CLASS__ ) :
								strtoupper ( explode ( $context, $PARTS [ 0 ] ) [ 0 ] );
		$method_name		=	( $context == '' ) ? strtoupper ( $PARTS [ 0 ] ) :
								strtoupper ( explode ( $context, $PARTS [ 0 ] ) [ 1 ] );
		$request_keys		= $me->request_keys;
		$request			= $me->request;
		$declarations		= $me->declarations;
		$declarations_first	= $me->declarations_first;
		$mode				= $me->mode;
		$action				= $me->action;
		$result				= SELF::FAIL;
		$parameters			= [];
		
		// 2. IF...
		if
		(
			// THE METHOD DOES NOT EXIST AND...
				!method_exists ( $method_class, $method_name ) &&
				
			// THE METHOD NAME IS AN ALIAS OF ANOTHER METHOD AND...
				array_key_exists ( $method_name, SELF::$ALIAS ) &&
				
			// THE METHOD OF THIS ALIAS EXISTS
				method_exists ( $method_class, SELF::$ALIAS[$method_name] )
		)
		{
			// USED THE ALIASED METHOD NAME
			$method_name = SELF::$ALIAS[$method_name];
		}
		
		// 3. SET STRICT MODE
		$STRICT_MODE =	(
							// IF USER OVERRIDE IS NOT FALSE (OR NO USER OVERRIDE) AND...
							!( count ( $PARTS ) > 1 && stripos ( $PARTS [ 1 ], 'false' ) !== false ) &&
							
							// NO DEFAULT SETTINGS FOUND OR...
							!in_array ( $method_name, SELF::$NO_STRICT_MODE )
							
							// IF USER OVERRIDE EXISTS AND USER OVERRIDE IS TRUE
						) || ( count ( $PARTS ) > 1 && stripos ( $PARTS [ 1 ], 'true' ) !== false );
		
		// 4. ADJUST THE MODE SETTINGS
		if
		(
			// CHECK IF THE METHOD NAME IS IN THE LIST AND...
				array_key_exists ( $method_name, SELF::$METHOD_SETTINGS ) &&
				
			// CHECK IF THE MODE SELECTED IS IN ITS LIST OF ACCEPTED VALUES
				!in_array ( $mode, SELF::$METHOD_SETTINGS [ $method_name ] )
		)
		{
			// 4.1. SET TO DEFAULT IF THE VALUE IS WRONG
			$mode = SELF::$METHOD_SETTINGS [ $method_name ] [ 0 ];
		}
		if ( $mode < 1 || $mode > 4 ) { $mode = 1; }
		
		// 5. IF DECLARATIONS ARE REQUIRED FIRST, INSERT VALUES TO PARAMETERS
		if ( $declarations_first || $mode % 2 == 0 )
		{
			$parameters = $declarations;
		}
		
		// 6. CHECK IF THE CURRENT REQUEST METHOD MATCHES THE REQUIRED METHOD
		if
		(
				strtoupper ( $request ) == 'REQUEST' ||
				strtoupper ( $request ) == $_SERVER['REQUEST_METHOD']
		)
		{
			// 6.1. SORT THE REQUEST KEYS
			$key_list = [];
			$exceptions = [];
			foreach ( SELF::DISASSEMBLE ( $request_keys ) as $key )
			{
				// 6.1.1. IF THE KEY STARTS WITH A PUNCTUATION MARK...
				if ( substr( $key, 0, 1 ) === '!' || in_array ( $key, $this->VARIABLES['IGNORE'] ) )
				{
					// 6.1.1.1. ADD IT TO THE EXCEPTIONS
					$exceptions[] = ltrim ( $key, '!' );
				}
				// 6.1.2. OTHERWISE...
				else
				{
					// 6.1.2.1. ADD IT TO THE LIST OF KEYS TO BE USED
					$key_list[] = $key;
				}
			}
			
			// 6.2. READ THE LIST OF KEYS
			foreach ( $key_list as $key )
			{
				// 6.2.1. CHECK IF KEY EXISTS IN REQUEST
				if ( array_key_exists ( $key, $_REQUEST ) )
				{
					// 6.2.1.1. ASSIGN THE REQUEST VALUE INTO THE PARAMETERS IF
					//			DATA FROM REQUEST SHOULD BE PASSED AS PARAMETERS
					if ( $mode % 2 == 1 ) $parameters[] = $_REQUEST [ $key ];
				}
				// 6.2.2. ELSE IF REQUESTED KEYS ARE MANDATORY AND KEYS ARE NOT FOUND
				else if ( $mode > 2 )
				{
					// 6.2.2.1. RETURN A FAILURE NOTICE IF KEY IS NOT FOUND
					return SELF::FAIL;
				}
			}
			
			// 6.3. READ THE LIST OF KEYS IN REQUEST
			foreach ( array_keys ( $_REQUEST ) as $key )
			{
				// 6.3.1. CHECK IF THE KEY IS NOT REQUESTED
				if ( !in_array ( $key, $key_list ) )
				{
					// 6.3.1.1. TERMINATE IF MODE IS STRICT
					if ( $STRICT_MODE && !in_array ( $key, $exceptions ) )
					{
						return SELF::FAIL;
					}
					
					// 6.3.1.2. ADD THE KEY TO THE LIST OF
					//			KEYS THAT WOULD BE SKIPPED
					$this->VARIABLES['SKIP_ONCE'][] = $key;
				}
			}
		}
		else
		{
			// 6.4. RETURN A FAILURE NOTICE IF METHOD IS NOT FOUND
			return SELF::FAIL;
		}
		
		// 7. IF DECLARATIONS ARE NOT PRIORITIZED AND
		//	  DATA FROM REQUEST ARE PASSED AS PARAMETERS,
		//	  THEN ADD IT AT THE END OF THE PARAMETERS
		if ( !$declarations_first && $mode % 2 == 1 )
		{
			// 7.1. READ THE DECLARATIONS BASED ON INDEX KEYS
			for ( $key = 0; $key < count ( $declarations ); $key++ )
			{
				// 7.1.1. ASSIGN THE DECLARATION VALUE INTO THE PARAMETERS
				$parameters[] = $declarations [ $key ];
			}
		}
		
		// 8. TRY TO EXECUTE THE METHOD
		$result = SELF::EXECUTE ( $method_name, $parameters, $action, $method_class, $context, $class_instance );
		
		// 9. DIE AFTER METHOD EXECUTION ( DO NOT RETURN VALUE )
		if ( SELF::$DEATH && $result !== SELF::FAIL )
		{
			die;
		}
		
		// 10. RETURN A WARNING IF EVERYTHING FAILS
		//     OR THE RESULTS IF EVERYTHING SUCCEEDS
		return $result;
	}
	
	/*///------------------------------------------------------------------------
			>>> LOAD SCRIPT RESOURCE
	/*///------------------------------------------------------------------------
	FINAL PUBLIC FUNCTION SCRIPT()
	{
		// IF...
		if
		(
			// THE NUMBER OF ARGUMENT IS LESS THAN 1 OR...
				func_num_args() < 1  ||
				
			// THE 1ST ARGUMENT IS NOT A STRING OR...
				!is_string ( func_get_args()[0] ) ||
				
			// THE 1ST ARGUMENT IS BEING VALIDATED AS A STRING BUT IS NUMERIC
				is_numeric ( func_get_args()[0] ) 
		)
		{
			// SCRIPT FAILED
			return SELF::FAIL;
		}
		
		// USE THE FIRST ARGUMENT AS THE METHOD NAME
		$methodname = strtoupper ( func_get_args()[0] );
		
		// GATHER THE REST OF THE ARGUMENTS AS PARAMETERS TO THE METHOD CALLED
		$parameters = [];
		if ( func_num_args() > 1 )
		{
			for ( $ctr = 1; $ctr < func_num_args(); $ctr++ )
			{
				$parameters[] = func_get_args() [ $ctr ];
			}
		}
		
		// RUN THE METHOD
		return $this->EXECUTE ( $methodname, $parameters, NULL, 'SCRIPTS' );
	}
	
	/*///------------------------------------------------------------------------
			>>> EXECUTE THE METHOD
	/*///------------------------------------------------------------------------
	FINAL PRIVATE FUNCTION EXECUTE
	(
			string $method_name,			// METHOD NAME
			array $parameters = [],			// METHOD PARAMETERS
			closure $action = NULL,			// ANONYMOUS FUNCTION
			string $classname = __CLASS__,	// CLASS NAME OF METHOD
			string $method_context = '',	// '::' FOR STATIC, '->' FOR OBJECT, '' FOR BOTH
			&$class_instance = null			// INSTANCE OF CLASS
	)
	{
		// CHECK IF...
		if
		(
			// THE METHOD NAME EXISTS AS A METHOD OF THIS CLASS AND...
				method_exists ( $classname, $method_name ) &&
				
			// THE METHOD NAME CAN BE CALLED
				is_callable ( $classname, $method_name )
		)
		{
			// PREPARE THE METHOD FOR CHECKING ADDITIONAL INFORMATION ABOUT IT
			$method = new ReflectionMethod ( $classname, $method_name );
			
			// GET THE NUMBER OF REQUIRED AND TOTAL PARAMETERS OF METHOD
			$required	= $method->getNumberOfRequiredParameters();
			$total		= $method->getNumberOfParameters();
			
			// CHECK...
			if
			(
				// IF THE NUMBER OF PARAMETERS MEETS THE REQUIREMENTS AND...
					count ( $parameters ) >= $required &&
					
				// THE METHOD IS PUBLIC AND...
					$method->isPublic() &&
					
				// IF EITHER TOTAL IS 0, OR IF THE PARAMETERS ARE ACCEPTED BY THE METHOD CALLED AND...
					( $total == 0 || SELF::ACCEPTED ( $parameters, $method_name, $classname ) ) &&
					
				// IF...
					(
						// METHOD CONTEXT IS NOT SPECIFIED OR...
							$method_context == '' ||
							
						// METHOD CONTEXT IS OBJECT AND THE DETECTED CONTEXT IS OBJECT OR...
							( $method_context == '->' && !$method->isStatic() ) ||
							
						// METHOD CONTEXT IS STATIC AND THE DETECTED CONTEXT IS STATIC
							( $method_context == '::' && $method->isStatic() )
					)
			)
			{
				// IF AN ANONYMOUS FUNCTION IS NOT DECLARED, USE THE DEFAULT
				if ( $action === NULL )
				{
					$action = SELF::$ANON_METHOD;
				}
				
				// PREPARE TO HANDLE RESULT
				$result = null;
				
				// IF OBJECT CONTEXT...
				if ( $method_context == '->' || $class_instance !== null )
				{
					// INSTANTIATE CLASS
					if ( $class_instance === null ) $class_instance = new $classname;
					
					// RUN THE METHOD AND GET THE RESULT
					$result = $class_instance->$method_name( ...$parameters );
				}
				// ELSE IF CONTEXT IS NOT SPECIFIED AND DETECTED CONTEXT IS OBJECT
				else if ( $method_context == '' && !$method->isStatic() )
				{
					// RUN IN OBJECT CONTEXT
					$result = $this->$method_name( ...$parameters );
				}
				else
				{
					// RUN STATICALLY
					$result = call_user_func_array
					(
						array ( $classname, $method_name ), $parameters
					);
				}
				
				// PROCESS THE RESULT FURTHER USING THE ANONYMOUS FUNCTION
				return ( $action ) ( $result );
			}
		}
		
		// RETURN IF FAIL
		return SELF::FAIL;
	}
	
	/*///------------------------------------------------------------------------
			>>> RECEIVE THE ARGUMENTS
	/*///------------------------------------------------------------------------
	FINAL PUBLIC STATIC FUNCTION ARGS
	(
			array &$args,			// ARGUMENT ARRAY ( FROM func_get_args() )
			string $type = '',		// VARIABLE TYPES REQUIRED
			array $default = [],	// DEFAULT VALUES ON OPTIONAL PARAMETERS
			string $names = ''		// OPTIONAL PREFERRED NAMES FOR ARRAY ELEMENTS
	): BOOL
	{
		$type = explode( ",", str_replace ( ' ', '', $type ) );
		$limit = count ( $type );
		$argsLimit = count ( $args );
		$numberRequired = $limit - count ( $default );
		
		// CONVERT VARIABLE TYPES INTO THEIR PROPER TYPES
		for ( $ptr = 0; $ptr < $limit; $ptr++ )
		{
			$type[$ptr] = strtoupper ( $type[$ptr] );
			if ( $type[$ptr] == 'INT' )
			{
				$type[$ptr] = 'INTEGER';
			}
			else if ( $type[$ptr] == 'FLOAT' )
			{
				$type[$ptr] = 'DOUBLE';
			}
			else if ( $type[$ptr] == 'BOOL' )
			{
				$type[$ptr] = 'BOOLEAN';
			}
			else if ( $type[$ptr] == 'METHOD' )
			{
				$type[$ptr] = 'CLOSURE';
			}
			else if ( $type[$ptr] == 'FUNCTION' )
			{
				$type[$ptr] = 'CLOSURE';
			}
			else if ( $type[$ptr] == 'ANY' )
			{
				$type[$ptr] = 'ALL';
			}
		}
		
		// THE NUMBER OF ARGUMENTS MUST ALWAYS BE LESS
		// OR EQUAL TO THE NUMBER OF VARIABLE TYPES AND
		// GREATER THAN OR EQUAL TO THE NUMBER OF ARGUMENTS
		// REQUIRED. THIS IS A NAIVE CHECK BECAUSE WE HAVE
		// NOT CHECKED THE VARIABLE TYPES YET.
		if ( $argsLimit <= $limit && $argsLimit >= $numberRequired )
		{
			// CONVERT NAMES INTO ARRAY
			$names = explode( ",", str_replace ( ' ', '', $names ) );
			
			// PREPARE THE LIST OF VARIABLES THAT PASSED
			$passed = [];
			
			// INITIALIZE THE INDEX OF TYPE
			$typePtr = 0;
			
			// SCAN VARIABLES BASED ON INDEX
			for ( $ctr = 0; $ctr < $argsLimit; $ctr++ )
			{
				// GET THE CURRENT VARIABLE TYPE
				$thisvar_type = SELF::VAR_TYPE ( $args[$ctr] );
				
				// ANALYZE IF THE VARIABLE MATCHES THE ACCEPTED TYPES
				while ( $typePtr < $limit )
				{
					if
					(
						// IF THIS VARIABLE TYPE IS IN THE LIST OF ACCEPTED TYPES YET OR...
							$type[$typePtr] == 'ALL'			||
							$type[$typePtr] == $thisvar_type	||
							(
								// THIS VARIABLE IS NULL AND...
									$thisvar_type == 'NULL'		&&
								
								// THE TYPE ACCEPTED IS A CLOSURE
									$type[$typePtr] == 'CLOSURE'
							)
					)
					{
						break;
					}
					$typePtr++;
				}
				
				// IF TYPE POINTER EXCEEDS TYPE LIMIT,
				// THIS MEANS ONE VARIABLE IS AN UNKNOWN TYPE
				if ( $typePtr >= $limit )
				{
					return false;
				}
				
				// IF THE CURRENT VARIABLE TYPE MATCHES THE REQUIRED TYPE,
				// RECORD THE INDEX OF THE VARIABLE THAT PASSED USING ITS
				// SUPPOSED INDEX IN THE REQUIRED VARIABLE TYPES
				if
				(
					// IF THIS VARIABLE TYPE IS IN THE LIST OR...
						$type[$typePtr] == 'ALL'			||
						$type[$typePtr] == $thisvar_type	||
						(
							// THIS VARIABLE IS NULL AND...
								$thisvar_type == 'NULL' &&
							
							// THE TYPE ACCEPTED IS A CLOSURE
								$type[$typePtr] == 'CLOSURE'
						)
				)
				{
					// THIS VARIABLE PASSED
					$passed[$typePtr] = $args[$ctr];
					
					// IF THE STRING IS NUMERIC...
					if ( $thisvar_type == 'INTEGER' || $thisvar_type == 'DOUBLE' )
					{
						// CONVERT NUMERIC STRINGS TO NUMBERS
						$passed[$typePtr] = (float)$passed[$typePtr];
					}
					
					// PROCEED TO THE NEXT POINTER
					$typePtr++;
				}
				
				// IF THE VARIABLE NEXT TO BE CHECKED IS PAST THE NUMBER REQUIRED
				// AND THE NUMBER OF VARIABLES THAT PASSED THE EVALUATION IS NOT
				// YET MET, THEN THE EVALUATION FAILED SINCE ALL REQIRED VARIABLES
				// MUST ALWAYS BE ON THE LEFT-SIDE / DECLARED FIRST.
				if ( $ctr + 1 >= $numberRequired && count ( $passed ) < $numberRequired )
				{
					return false;
				}
			}
			
			// SET THE REMAINING PARAMETERS TO THEIR DEFAULT VALUES
			for ( $ctr = $numberRequired; $ctr < $limit; $ctr++ )
			{
				// COMPUTE THE INDEX FOR DEFAULT VALUES
				$default_index = $ctr - $numberRequired;
				
				// VARIABLES THAT PASSED SHOULD HAVE THE
				// SAME INDEX AS THEIR VARIABLE TYPES
				// IF THESE INDICES ARE NOT SET, THEN
				// ASSIGN THE DEFAULT VALUE TO THAT INDEX
				if
				(
						!array_key_exists ( $ctr, $passed ) &&
						array_key_exists ( $default_index, $default )
				)
				{
					$passed[$ctr] = $default[$default_index];
				}
			}
			
			// TO COMPENSATE FOR THE NAIVE CHECK, WE MUST CHECK THE NUMBER OF ELEMENTS
			// THAT PASSED PLUS THE VARIABLES WITH DEFAULT VALUES. SINCE THESE TWO ARE
			// ALREADY MERGED IN "$passed", THIS ARRAY MUST MATCH THE LIMIT ( THE NUMBER
			// OF VARIABLE TYPES DECLARED).
			if ( count ( $passed ) !== $limit )
			{
				// RETURN FALSE ON MISMATCH
				return false;
			}
			
			// ORGANIZE AND ASSIGN THE VALUES
			$args = [];
			for ( $ctr = 0; $ctr < count ( $passed ); $ctr++ )
			{
				if
				(
						array_key_exists ( $ctr, $names ) &&
						!empty ( $names[$ctr] ) &&
						is_string ( $names[$ctr] )
				)
				{
					$args[$names[$ctr]] = $passed[$ctr];
				}
				else
				{
					$args[] = $passed[$ctr];
				}
			}
			
			// PUT THE ARGUMENTS IN A DATA STORAGE
			$args = SELF::STASH($args);
		}
		else
		{
			// ON INVALID ARGUMENTS, RETURN FALSE
			return false;
		}
		
		// RETURN TRUE IF EVERYTHING SUCCEEDS
		return true;
	}
	
	/*///------------------------------------------------------------------------
			>>> FIND THE CALLING METHOD'S NAME AND CLASS
	/*///------------------------------------------------------------------------
	FINAL PUBLIC STATIC FUNCTION CALLER
	(
			int $offset = 2	// DEFAULT OFFSET/INDEX OF CALLING FUNCTION
	): ARRAY
	{
		$trace = debug_backtrace();
		$return = [ 'function'=>'', 'class'=>'' ];
		
		// GET CALLER FUNCTION IF IT EXISTS ( IF INDEX 2 OR '$offset' EXISTS )
		if
		(
				count ( $trace ) > $offset
				&& array_key_exists ( 'function', ( $caller = $trace[$offset] ) ) 
		)
		{
			// GET THE FUNCTION NAME
			$return  = [ 'function' => $caller['function'] ];
			
			// GET THE CLASS NAME IF IT EXISTS
			if ( array_key_exists ( 'class', $caller ) )
			{
				$return [ 'class' ] = $caller [ 'class' ];
			}
		}
		
		// RETURN THE TRACED FUNCTION
		return $return;
	}
	
	/*///------------------------------------------------------------------------
			>>> CHECK IF A METHOD ACCEPTS THE LISTED PARAMETERS
	/*///------------------------------------------------------------------------
	FINAL PUBLIC STATIC FUNCTION ACCEPTED
	(
			array &$parameters,			// PARAMETER ARRAY TO BE CHECKED
			string $method_name,		// NAME OF METHOD TO BE CHECKED
			string $class = __CLASS__	// CLASS WHERE THE METHOD BELONGS TO
	): BOOL
	{
		// ANALYZE THE METHOD
		$info = SELF::METHOD_PARAMETERS_INFO ( $method_name, $class );
		
		// IF THE TOTAL PARAMETERS IS ZERO, ALLOW METHOD EXECUTION ( ASSUME NO CONFLICT )
		if ( $info->TOTAL == 0 ) return true;
		
		// CHECK IF THE NUMBER OF PARAMETERS DOES NOT EXCEED THE TOTAL PARAMETERS OF METHOD
		// AND CHECK IF THE NUMBER OF PARAMETERS IS GREATER THAN THE REQUIRED NUMBER
		if ( ( $cnt = count ( $parameters ) ) <= $info->TOTAL && $cnt >= $info->REQUIRED )
		{
			$accepted = [];	// STORES THE REORGANIZED PARAMETERS
			$used = [];		// STORES THE INDEX OF THE USED PARAMETERS
			
			// START READING THE LIST OF PARAMETERS NEEDED BY THE METHOD
			for ( $infctr = 0; $infctr < $info->TOTAL; $infctr++ )
			{
				// GET THE REQUIRED PARAMETERS
				$requiredType = strtoupper ( $info->PARAMETERS[$infctr]['TYPE'] );
				
				// START READING THE PARAMETERS PROVIDED BY THE USER
				for ( $ctr = 0; $ctr < $cnt; $ctr ++ )
				{
					// CHECK THE TYPE OF VARIABLE
					$currentType = SELF::VAR_TYPE ( $parameters [ $ctr ] );
					
					// CHECK IF...
					if
					(
						// THE INDEX IS NOT YET IN THE LIST
						// OF PARAMETERS THAT'S ALREADY USED AND..
							!in_array ( $ctr, $used ) &&
							(
								// THE TYPE OF VARIABLE MATCHES THE REQUIRED OR...
									$requiredType == $currentType ||
									stripos ( $requiredType, $currentType ) !== false ||
									stripos ( $currentType, $requiredType ) !== false ||
								// THE REQUIRED TYPE IS NULL ( OR ANY VARIABLE TYPE )
									$info->PARAMETERS[$infctr]['TYPE'] == NULL
							)
					)
					{
						// ACCEPT THE PARAMETER
						$accepted[$infctr] = $parameters [ $ctr ];
						
						// LIST ITS INDEX AS USED
						$used[] = $ctr;
						
						// EXIT LOOP
						break;
					}
				}
				
				// IF THE NUMBER OF ACCEPTED DID NOT CHANGE, AND THE CURRENT PARAMETER IS OPTIONAL...
				if ( count ( $accepted ) < $infctr + 1 && $info->PARAMETERS[$infctr]['IS_OPTIONAL'] )
				{
					// USE THE DEFAULT VALUE
					$accepted[$infctr] = $info->PARAMETERS[$infctr]['DEFAULT_VALUE'];
				}
			}
			
			// IF THE NUMBER OF ACCEPTED MATCHES THE TOTAL PARAMETERS
			if ( count ( $accepted ) == $info->TOTAL )
			{
				// USE THE ACCEPTED PARAMETERS AND RETURN
				$parameters = $accepted;
				return true;
			}
		}
		
		// RETURN FALSE ON FAILURE
		return false;
	}
	
	/*///------------------------------------------------------------------------
			>>> RETURNS DETAILED INFORMATION ABOUT A METHOD'S PARAMETERS
	/*///------------------------------------------------------------------------
	FINAL PUBLIC STATIC FUNCTION METHOD_PARAMETERS_INFO
	(
			string $method_name,		// METHOD TO ANALYZE
			string $class = __CLASS__	// CLASS WHERE METHOD BELONGS TO
	)
	{
		$return = [];
		$method = new ReflectionMethod($class, $method_name);
		$return['NAME'] = $method_name;
		$return['CLASS'] = $class;
		$return['RETURN_TYPE'] = $method->getReturnType();
		$return['PARAMETERS'] = [];
		$return['OPTIONAL'] = 0;
		$return['REQUIRED'] = 0;
		$return['TOTAL'] = 0;
		foreach ( $method->getParameters() as $parameter )
		{
			$defaultVal = '';
			$return['TOTAL']++;
			
			if ($parameter->isDefaultValueAvailable())
			{
				$defaultVal = $parameter->getDefaultValue();
				$return['OPTIONAL']++;
			}
			else
			{
				$return['REQUIRED']++;
			}
			
			$info =
			[
				'NAME' => $parameter->name,
				'TYPE' => $parameter->getType(),
				'IS_POINTER' => $parameter->isPassedByReference(),
				'IS_VARIADIC' => $parameter->isVariadic(),
				'IS_OPTIONAL' => $parameter->isOptional(),
				'DEFAULT_VALUE' => $defaultVal
			];
			
			$return['PARAMETERS'][] = $info;
		}
		return SELF::STASH ( $return );
	}
	
	/*///------------------------------------------------------------------------
			>>> CREATE DATA STORAGE
	/*///------------------------------------------------------------------------
	FINAL PUBLIC STATIC FUNCTION STASH
	(
			ARRAY $ARGS = [] // PARAMETERS TO BE STORED IN THE ANONYMOUS CLASS
	)
	{
		// RETURN A NEW ANONYMOUS CLASS
		RETURN NEW CLASS($ARGS)
		{
			PRIVATE STATIC $ANONYMOUS_CLASS_PARAMETERS = [];
			
			FINAL PUBLIC FUNCTION __CONSTRUCT()
			{
				SELF::STORE ( ...FUNC_GET_ARGS() );
			}
			
			FINAL PUBLIC FUNCTION __INVOKE()
			{
				SELF::STORE ( ...FUNC_GET_ARGS() );
			}
			
			FINAL PRIVATE STATIC FUNCTION STORE()
			{
				$ARGUMENTS = FUNC_GET_ARGS();
				IF ( FUNC_NUM_ARGS() == 1 && IS_ARRAY ( $ARGUMENTS[0] ) )
				{
					// STORE THE ARGUMENTS IF IT IS AN ARRAY
					SELF::$ANONYMOUS_CLASS_PARAMETERS = $ARGUMENTS[0];
				}
			}
			
			FINAL PUBLIC FUNCTION __GET
			(
					STRING $KEY // ARRAY INDEX OR KEY
			)
			{
				IF ( ARRAY_KEY_EXISTS ( $KEY, SELF::$ANONYMOUS_CLASS_PARAMETERS ) )
				{
					// IF THE ARRAY KEY EXISTS, RETURN THE VALUE
					RETURN SELF::$ANONYMOUS_CLASS_PARAMETERS [ $KEY ];
				}
				
				// OTHERWISE JUST RETURN NULL
				RETURN NULL;
			}
			
			FINAL PUBLIC FUNCTION __SET
			(
					STRING $KEY,	// ARRAY INDEX OR KEY
					$VALUE			// VALUE TO BE STORED
			)
			{
				// INSERT THE VALUE INTO THE ARRAY
				SELF::$ANONYMOUS_CLASS_PARAMETERS [ $KEY ] = $VALUE;
			}
		};
	}
	
	/*///------------------------------------------------------------------------
			>>> TRACE ERRORS IN PHP SCRIPT
	/*///------------------------------------------------------------------------
	FINAL PUBLIC STATIC FUNCTION BUG_TRACE
	(
			string $error_message = 'Unknown error found.' // DEFAULT ERROR MESSAGE IF DEBUG IS OFF
	)
	{
		// CREATE BACKTRACE
		$trace = debug_backtrace();
		
		// FIND THE PARENT FUNCTION
		$key = count ( $trace ) - 1;
		
		// PREPARE ERROR DETAILS
		$error_message .=	" An incorrect input in class " .
							"<b>{$trace [ $key ] [ 'class' ]}</b>, file " .
							"<b>{$trace [ $key ] [ 'file' ]}</b>, line " .
							"<b>{$trace [ $key ] [ 'line' ]}</b>, triggered an error ";
		
		// CHECK IF DEBUG IS ON
		if ( SELF::$DEBUG )
		{
			// THROW ERROR MESSAGE WITH COMPLETE DETAILS
			trigger_error ( $error_message, E_USER_NOTICE );
		}
		
		// SAVE AN ERROR MESSAGE
		SELF::$STATIC['ERROR'] = "{$error_message} in <b>{$trace [ 0 ] [ 'file' ]}</b>.";
	}
	
	
