*		BASIC ARRAY AND STRING OPERATIONS
	-----------------------------------------------------------------------------
	/*///------------------------------------------------------------------------
	
	
	/*///------------------------------------------------------------------------
			>>> SPLIT STRING INTO AN ARRAY OF CHARACTERS
	/*///------------------------------------------------------------------------
	FINAL PUBLIC STATIC FUNCTION MB_STR_SPLIT
	(
			string $string	// STRING TO BE PROCESSED
	): ARRAY
	{
		return preg_split ( '/(?<!^)(?!$)/u', $string );
	}
	
	/*///------------------------------------------------------------------------
			>>> LIMIT EACH WORD IN STRING TO NUMBER OF PIXELS
	/*///------------------------------------------------------------------------
	FINAL PUBLIC STATIC FUNCTION PX_PER_WORD
	(
			string $string,					// STRING TO BE PROCESSED
			float $pxlimit,					// NUMBER OF PIXELS
			float $fontsize = 16,			// FONT SIZE USED
			float $boldOffset = 1,			// CHARACTERS ARE BOLD?
			string $separator = '<br/>',	// STRING SEPARATOR
			bool $countSpaces = false		// COUNT SPACE
	)
	{
		if ( $string == '' ) return $string;
		$result = '';
		$multiplier = $fontsize / 16;
		$charsize =
		[
			4		=> 'il',
			4.5		=> 'j.',
			5		=> 'f',
			5.5		=> 'tI',
			6		=> 'rJ!',
			7		=> 'csz',
			8		=> 'aekovxyFLP0123456789',
			9		=> 'bdghnpquETY',
			9.5		=> 'ABKRSVXZ',
			10		=> 'CHNU',
			10.5	=> 'DGOQ',
			12		=> 'wM',
			14		=> 'mW'
		];
		
		$pxlen = 0;
		
		foreach ( explode ( ' ', $string ) as $word )
		{
			foreach ( SELF::MB_STR_SPLIT ( $word ) as $char )
			{
				$px = ( 16 + $boldOffset ) * $multiplier;
				
				if ( mb_detect_encoding ( $char ) != 'UTF-8' )
				{
					$px = ( 14 + $boldOffset ) * $multiplier;
					
					if ( ctype_lower ( $char ) )
					{
						$px = ( 10 + $boldOffset ) * $multiplier;
					}
					
					foreach ( $charsize as $size => $charlist )
					{
						if ( $char != '' && strpos ( $charlist, $char ) !== false )
						{
							$px = ( $size + $boldOffset ) * $multiplier;
							break;
						}
					}
				}
				
				if ( $pxlen >= $pxlimit )
				{
					$pxlen = 0;
					$result .= $separator;
				}
				
				$pxlen += $px;
				$result .= $char;
			}
			
			$result .= ' ';
			$pxlen = $countSpaces ? $pxlen + ( ( 10 + $boldOffset ) * $multiplier ) : 0;
		}
		
		return trim ( $result );
	}
	
	/*///------------------------------------------------------------------------
			>>> SHORTEN THE GIVEN STRING
	/*///------------------------------------------------------------------------
	FINAL PUBLIC STATIC FUNCTION SHORTEN_STRING
	(
			string $string,		// STRING TO BE PROCESSED
			int $maxlength,		// LENGTH OF STRING NEEDED
			int $start = 0		// STARTING INDEX
	): STRING
	{
		// GET THE STRING'S CHARACTER ENCODING
		$encoding = mb_detect_encoding($string);
		
		// MEASURE THE LENGTH RELATIVE TO CHARACTER ENCODING
		if ( mb_strlen ( $string, $encoding ) > $maxlength )
		{
			// GET THE REQUIRED STRING
			$string = mb_substr ( $string, $start, $maxlength, $encoding );
		}
		
		// RETURN THE PROCESSED STRING
		return $string;
	}
	
	/*///------------------------------------------------------------------------
			>>> RETURN THE CONTENT OF ARRAY
	/*///------------------------------------------------------------------------
	FINAL PUBLIC STATIC FUNCTION ARRAY_KEY
	(
			array $array,		// ARRAY TO BE PROCESSED
			string $array_key,	// ARRAY INDEX OR KEY
			$default = NULL		// DEFAULT VALUE TO RETURN IF KEY IS NOT FOUND
	)
	{
		return ( array_key_exists ( $array_key, $array ) ? $array[$array_key] : $default );
	}
	
	/*///------------------------------------------------------------------------
			>>> RETURN MULTIPLE CONTENTS FROM ARRAY
	/*///------------------------------------------------------------------------
	FINAL PUBLIC STATIC FUNCTION ARRAY_KEY_ARRAY
	(
			array $array,		// ARRAY TO BE READ
			string $array_keys,	// ARRAY INDEX OR KEY
			$default = NULL		// DEFAULT VALUE TO RETURN IF KEY IS NOT FOUND
	)
	{
		// PREPARE RESULT
		$result = [];
		
		// EXTRACT DATA FROM REQUEST
		foreach ( SELF::DISASSEMBLE( $array_keys ) as $array_key )
		{
			$result[] = SELF::ARRAY_KEY ( $array, $array_key, $default );
		}
		
		// RETURN RESULT
		return $result;
	}
	
	/*///------------------------------------------------------------------------
			>>> SEPARATE VARIABLES USING CHARACTER AND TRIM SPACES
	/*///------------------------------------------------------------------------
	FINAL PUBLIC STATIC FUNCTION DISASSEMBLE
	(
			string $frame,		// STRING TO PROCESS
			string $joint = ','	// SEPARATE ON THIS CHARACTER/STRING
	): ARRAY
	{
		// PREPARE VARIABLES
		$result = [];
		
		// TRIM THE JOINT FROM BOTH ENDS IF THERE ARE ANY AND THEN SEPARATE
		foreach ( explode( $joint, trim ( trim ( $frame, $joint ) ) ) as $parts )
		{
			// TRIM WHITESPACES OF PARTS
			$result[] = trim($parts);
		}
		
		// RETURN THE RESULT
		return $result;
	}
	
	/*///------------------------------------------------------------------------
			>>> CREATE A STRING OF THE DECLARED STRING OR CHARACTER
	/*///------------------------------------------------------------------------
	FINAL PUBLIC STATIC FUNCTION STRING_OF
	(
			string $string,		// STRING TO PROCESS
			int $repetition = 1	// NUMBER OF TIMES REPETITION
	): STRING
	{
		// PREPARE THE RESULT
		$result = '';
		
		// BEGIN ADDING STRING TO RESULT
		for ($ctr = 1; $ctr <= $repetition; $ctr++)
		{
			$result .= $string;
		}
		
		// RETURN THE FINAL RESULT
		return $result;
	}
	
	/*///------------------------------------------------------------------------
			>>> SPLIT A STRING INTO EITHER AN ARRAY OF STRINGS WITH SPECIFIED
				LENGTH OR RETURN A STRING SEPARATED BY THE STATED SEPARATOR
	/*///------------------------------------------------------------------------
	FINAL PUBLIC STATIC FUNCTION SPLIT_BY_LENGTH
	(
			string $string,			// STRING TO PROCESS
			int $length = 1,		// LENGTH PER ELEMENT
			string $separator = '',	// CHARACTER/STRING TO USE AS SEPARATOR
			string $beginning = '',	// OPTIONAL STRING TO INSERT AT THE BEGINNING OF EACH LINE
			string $ending = ''		// OPTIONAL STRING TO INSERT AT THE END OF EACH LINE
	)
	{
		// PREPARE THE RESULTS
		$result = [];
		
		// START LOOPING THROUGH THE STRING USING THE LENGTH SPECIFIED
		for( $step = 0; $step < strlen( $string ); $step+=$length )
		{
			// STORE THE STRING WITH LENGTH "$length" INTO THE RESULT ARRAY
			$result[] = $beginning.substr ( $string, $step, $length ).$ending;
		}
		
		// IF STRING RESULT IS EXPECTED, RETURN AN EMPTY STRING IF RESULT IS EMPTY
		if ( count( $result ) === 0 && $separator !== '' )
		{
			return "";
		}
		
		// IF A SEPARATOR IS NOT SPECIFIED, RETURN AN ARRAY INSTEAD OF A STRING
		if ( $separator == '' )
		{
			return $result;
		}
		
		// RETURN A STRING BY DEFAULT
		return implode($separator, $result);
	}
	
	/*///------------------------------------------------------------------------
			>>> LOOP THROUGH ARRAY RECURSIVELY AND PERFORM FUNCTION
	/*///------------------------------------------------------------------------
	FINAL PUBLIC STATIC FUNCTION ARRAY_RECURSIVE_LOOP
	(
			array $array,				// ARRAY TO PROCESS
			closure $function = NULL	// ANONYMOUS FUNCTION
	)
	{
		// PREPARE RETURN VALUE
		$return = SELF::ERROR;
		
		// SET ANONYMOUS FUNCTION TO DEFAULT IF EMPTY
		if ( $function == NULL ) $function = SELF::$HANDLE_FILE;
		
		// CHECK IF FUNCTION IS VALID
		if ( SELF::VAR_TYPE ( $function ) == 'CLOSURE' )
		{
			// GET NUMBER OF PARAMETERS
			$mirror = new ReflectionFunction( $function );
			$parameters = $mirror->getNumberOfParameters();
			$requiredParameters = $mirror->getNumberOfRequiredParameters();
			
			// CHECK IF THE NUMBER OF PARAMETERS IS 1 OR MORE
			// AND IF THE REQUIRED PARAMETERS IS LESS THAN 2
			if ( $parameters >= 1 && $requiredParameters <= 2 )
			{
				// BEGIN LOOPING
				foreach ( $array as $name=>$value )
				{
					if ( is_array ($value) )
					{
						// RECURSE IF ARRAY
						$return = SELF::ARRAY_RECURSIVE_LOOP($value, $function);
					}
					else
					{
						// PREPARE THE ARGUMENTS
						$arguments = [$value];
						
						// IF THERE ARE MORE THAN 1 PARAMETERS, ADD THE NAME
						if ( $parameters > 1 ) { $arguments[] = $name; }
						
						// CALL ANONYMOUS FUNCTION
						$return = $function (...$arguments);
					}
				}
			}
			else
			{
				// THROW ERROR MESSAGE AND RETURN FALSE
				SELF::$STATIC['ERROR'] =	'Anonymous function needs to have at least 1 '.
											'parameter [i.e. function($value){...} ]. '.
											'The first two parameters are reserved for $value '.
											'and $name [i.e. function($value, $name){...} ].';
				$return = SELF::ERROR;
			}
		}
		else
		{
			// THROW ERROR MESSAGE AND RETURN FALSE
			SELF::$STATIC['ERROR'] = 'Anonymous function is invalid.';
			$return = SELF::ERROR;
		}
		
		// RETURN TRUE IF SUCCESSFUL
		return $return;
	}
	
	/*///------------------------------------------------------------------------
			>>> CHANGE THE PATH SEPARATOR
	/*///------------------------------------------------------------------------
	FINAL PUBLIC STATIC FUNCTION STANDARD_PATH
	(
			string $path,						// PATH/URL TO FILE
			bool $use_system_separator = false	// USE YOUR OS'S PATH SEPARATOR?
	): STRING
	{
		// CHANGE BACKSLASHES TO FORWARD SLASH
		$path = str_replace('\\', '/', $path);
		
		if ($use_system_separator)
		{
			// CHANGE FORWARD SLASH TO THE SYSTEM'S PATH SEPARATOR
			$path = str_replace('/', PATH_SEPARATOR, $path);
		}
		
		// RETURN THE NEW PATH
		return $path;
	}
	
	/*///------------------------------------------------------------------------
			>>> STRIP HTTP/S
	/*///------------------------------------------------------------------------
	FINAL PUBLIC STATIC FUNCTION STRIP_HTTP
	(
			string $path // PATH/URL TO FILE
	): STRING
	{
		// RETURN THE NEW PATH
		return str_ireplace ( 'https://', '', str_ireplace ( 'http://', '', $path ) );
	}
	
	/*///------------------------------------------------------------------------
			>>> GET THE FOLDER PATH OF THE CURRENT SCRIPT
	/*///------------------------------------------------------------------------
	FINAL PUBLIC STATIC FUNCTION PATH(): STRING
	{
		// REMOVE SLASHES OF HOST
		$path = trim( "http://{$_SERVER['SERVER_NAME']}", '/' );
		
		// REMOVE SLASHES WHILE URL-ENCODING PER FOLDER
		foreach ( SELF::DISASSEMBLE ( dirname( $_SERVER['PHP_SELF'] ), '/' ) as $folder )
		{
			$path .= "/".rawurlencode ( trim ( $folder, '/' ) );
		}
		
		// MAKE SURE THAT THE PATH ENDS WITH A TRAILING SLASH AND THEN RETURN THE RESULT
		return trim ( $path, "/" )."/";
	}
	
	
