	/*///------------------------------------------------------------------------
			>>> DETECT SSL
	/*///------------------------------------------------------------------------
	FINAL PUBLIC STATIC FUNCTION HTTPS
	(
			string &$url = '',		// OPTIONAL VARIABLE TO STORE HTTPS URL TO
			int $https_port = 443	// OPTIONAL SSL PORT
	): BOOL
	{
		// CREATE HTTPS URL
		$url = "https://".rtrim
		(
			rtrim
			(
				$_SERVER [ 'HTTP_HOST' ], '/'
			), '\\'
		).$_SERVER [ 'REQUEST_URI' ];
		
		// RETURN TRUE IF CURRENT PAGE IS HTTPS
		return
		(
			// CHECK THE SERVER PORT FIRST
			$_SERVER['SERVER_PORT'] == $https_port ||
			// OR CHECK IF HTTPS IS ON
			(
				array_key_exists ( 'HTTPS', $_SERVER ) &&
				!empty ( $_SERVER [ 'HTTPS' ] ) &&
				$_SERVER [ 'HTTPS' ] !== 'off'
			)
		);
	}
	
	/*///------------------------------------------------------------------------
			>>> ENSURE SSL
	/*///------------------------------------------------------------------------
	FINAL PUBLIC STATIC FUNCTION SECURE
	(
			int $https_port = 443 // OPTIONAL SSL PORT
	)
	{
		if ( !SELF::HTTPS ( $url, $https_port ) )
		{
			header("location: {$url}");
		}
	}
	
