	/*///------------------------------------------------------------------------
			>>> COMPRESS THE DATA SENT BACK TO CLIENT
	/*///------------------------------------------------------------------------
	FINAL PUBLIC STATIC FUNCTION COMPRESS
	(
			string $array_keys = '',	// COMMA SEPARATED ARRAY INDICES OR KEYS OF $_REQUEST
			bool $mandatory_all = false	// SHOULD ALL KEYS BE PRESENT IN THE REQUEST?
	)
	{
		if
		(
			// NO ARRAY KEYS ARE REQUIRED OR...
				$array_keys == '' ||
				
			// MANDATORY KEYS SHOULD BE FOUND IN THE ARRAY OR...
				( $mandatory_all && SELF::ARRAY_KEYS_EXIST ( $_REQUEST, $array_keys ) ) ||
				
			// SOME KEYS ARE REQUIRED
				( !$mandatory_all && SELF::SOME_ARRAY_KEYS_EXIST ( $_REQUEST, $array_keys ) )
		)
		{
			// CHECK IF THE SERVER ACCEPTS GZIP ENCODING
			if ( substr_count ( $_SERVER["HTTP_ACCEPT_ENCODING"], "gzip" ) )
				ob_start("ob_gzhandler");
		}
	}
	
	
	/*///------------------------------------------------------------------------
	-----------------------------------------------------------------------------
*		FILE OPERATIONS
	-----------------------------------------------------------------------------
	/*///------------------------------------------------------------------------
	
	
	/*///------------------------------------------------------------------------
			>>> CONVERT BYTES TO READABLE SIZE
	/*///------------------------------------------------------------------------
	FINAL PUBLIC STATIC FUNCTION BYTES
	(
			float $size // SIZE IN BYTES
	): STRING
	{ 
		$range = [ ' Bytes', ' KB', ' MB', ' GB', ' TB', ' PB', ' EB', ' ZB', ' YB' ];
		return	$size ? round ( $size / pow ( 1024, ( $index = floor ( log ( $size, 1024 ) ) ) ), 2 ) .
				$range[$index] : '0 Bytes';
	}
	
