	/*///------------------------------------------------------------------------
			>>> GET DISK INFO
	/*///------------------------------------------------------------------------
	FINAL PUBLIC STATIC FUNCTION DISKS(): ARRAY
	{
		$fso = new COM ( 'Scripting.FileSystemObject' );
		$drives = $fso->Drives;
		$types = [ 'Unknown', 'Removable', 'Fixed', 'Network', 'CD-ROM', 'RAM Disk' ];
		$return = [];
		foreach ( $drives as $drive )
		{
			$driveInfo = $fso->GetDrive ( $drive );
			$letter = $driveInfo->DriveLetter . ":/";
			$name = "[Drive not ready]";
			$type = $types[$driveInfo->DriveType];
			$space = 0;
			$size = 0;
			
			if ( $driveInfo->DriveType == 3 )
			{
				$name = $driveInfo->Sharename;
			}
			else if ( $driveInfo->IsReady )
			{
				$name = $driveInfo->VolumeName;
				$space = $driveInfo->FreeSpace;
				$size = $driveInfo->TotalSize;
			}
			
			$return[] = [
							'letter'=>$letter,
							'name'	=>$name,
							'type'	=>$type,
							'space'	=>$space,
							'size'	=>$size
						];
		}
		
		return $return;
	}
	
	/*///------------------------------------------------------------------------
			>>> GET FOLDER CONTENT
	/*///------------------------------------------------------------------------
	FINAL PUBLIC STATIC FUNCTION FOLDER
	(
			string $dir = '',			// DIRECTORY
			closure $do = NULL,			// ANONYMOUS FUNCTION
			int $start = 1,				// STARTING INDEX
			int $end = 0,				// ENDING INDEX
			bool $showHidden = false,	// SHOW HIDDEN FILES
			bool $showSystem = false,	// SHOW HIDDEN FILES
			bool $countFiles = true		// COUNT NUMBER OF FILES?
	)
	{
		// REMOVE URL ENCODING
		if ( !file_exists ( $dir ) )
		{
			$dirString = rawurldecode ( $dir );
			$dirString = file_exists ( $dirString ) ? $dirString : urldecode ( $dir );
			$dirString = file_exists ( $dirString ) ? $dirString : urldecode ( rawurldecode ( $dir ) );
			$dirString = file_exists ( $dirString ) ? $dirString : rawurldecode ( urldecode ( $dir ) );
			$dir = file_exists ( $dirString ) ? $dirString : $dir;
		}
		
		// PREPARE RETURN ARRAYS (LIST OF FILES IN FOLDER AND LIST OF DATES)
		$return = [];
		$datelist = [];
		
		// INITIALIZE FILE COUNTER
		$index = 0;
		
		// CHECK IF OS IS WINDOWS
		$is_windows = ( strtoupper ( substr ( PHP_OS, 0, 3 ) ) === 'WIN' );
		
		// IF DIRECTORY IS EMPTY, USE THE CURRENT WORKING DIRECTORY BY DEFAULT
		if ( $dir === '' ) $dir = getcwd();
		
		// USE THE DEFAULT METHOD IF NO ANONYMOUS FUNCTION (FOR PROCESSING RETURN ARRAY) IS DECLARED
		if ( $do === NULL ) $do = SELF::$ANON_METHOD;
		
		// IF THE ADDRESS POINTS TO A DIRECTORY
		if ( is_dir ( $dir ) && ( $dh = opendir ( $dir ) ) )
		{
			// ENSURE STANDARD PATH / URL FORMAT
			$dir = rtrim ( SELF::STANDARD_PATH ( realpath ( $dir ) ), '/' ) . '/';
			
			// READ THE DIRECTORY CONTENTS
			while ( ( $file = readdir ( $dh ) ) !== false)
			{
				// IS THE FILE HIDDEN?
				$is_hidden = false;
				
				// IF THE OS IS WINDOWS
				if ( $is_windows )
				{
					// GET THE FILE ATTRIBUTES (CHECK IF FILE IS HIDDEN / SYSTEM)
					$attr = trim ( exec ( 'FOR %A IN ("' . $dir.$file . '") DO @ECHO %~aA' ) );
					$is_hidden = ( $attr[3] === 'h' );
					$is_system = ( $attr[4] === 's' );
				}
				
				// SHOULD THE FILE BE INCLUDED IN THE LIST?
				$show_file = true;
				
				// DO NOT LIST THE FILE IF IT IS HIDDEN/SYSTEM UNLESS FORCED ($showHidden/$showSystem)
				if ( $is_hidden && !$showHidden ) $show_file = false;
				if ( $is_system && !$showSystem ) $show_file = false;
				
				// SHOW THE '..' FOLDER
				if ( $file == '..' ) $show_file = true;
				
				// DO NOT SHOW THE '.' FOLDER
				if ( $file != '.' && $show_file )
				{
					// IF THE FILE IS POINTING TO '..' FOLDER, ADD A TRAILING '/'
					if ( $file == '..' )
					{
						$file = "$file/";
					}
					else
					{
						// IF THE CURRENT FILE IS A NORMAL FILE, COUNT
						$index += 1;
						
						// START LISTING ON '$start'
						if ( $index < $start ) continue;
						
						// END LISTING ON '$end'
						if ( $end > 0 && $index > $end )
						{
							// PROCEED IF COUNT FILES IS ON
							if ( $countFiles )
							{
								continue;
							}
							// STOP HERE IF THERE'S NO NEED TO COUNT THE NUMBER OF FILES
							else
							{
								break;
							}
						}
					}
					
					// INITIALIZE COUNTER TO THE RETURN ARRAYS
					$ctr = 0;
					
					// PREPARE THE FIRST FILE IN THE LIST
					$thisfile = rawurlencode ( $dir.$file );
					
					// READ THE MODIFIED DATE OF THE FIRST FILE IN THE LIST
					$thisFileDateModified = ( $file != '../' ) ? @filemtime ( $dir.$file ) : 0;
					
					// IF PROPERTY IS NOT FOUND, SET DATE TO 1
					if ( $thisFileDateModified === false ) $thisFileDateModified = 1;
					
					// TEMPORARY STORAGE FOR FILE LIST
					$temp = "";
					
					// TEMPORARY STORAGE FOR DATE MODIFIED (IN SECONDS)
					$modtemp = 0;
					
					// ITERATE THROUGH THE LIST OF DATES AND SORT FILE ACCORDING TO DATE
					foreach ( $datelist as $date )
					{
						// SORT BY DATE
						if ( $date < $thisFileDateModified && $date > 0 )
						{
							// TEMPORARILY STORE THE LISTED FILE ADDRESS AND DATE
							$temp = $return [ $ctr ];
							$modtemp = $date;
							
							// REPLACE THE LISTED FILE AND DATE WITH THE FILE HAVING THE LATEST DATE
							$return [ $ctr ] = $thisfile;
							$datelist [ $ctr ] = $thisFileDateModified;
							
							// STORE THE TEMPORARILY LISTED FILE AND DATE
							// TO BE COMPARED TO THE NEXT FILE IN THE LIST
							$thisfile = $temp;
							$thisFileDateModified = $modtemp;
						}
						
						// NEXT FILE IN THE RETURN ARRAY
						$ctr++;
					}
					
					// ADD THE LAST FILE IN THE LIST
					$return[] = $thisfile;
					$datelist[] = $thisFileDateModified;
				}
			}
			
			// CLOSE THE CURRENT DIRECTORY
			closedir ( $dh );
		}
		// IF THE ADDRESS POINTS TO A ZIP FILE
		else if ( is_file ( $dir ) && strtolower ( pathinfo ( $dir, PATHINFO_EXTENSION ) ) == 'zip' )
		{
			// PARSE THE PARENT PATH
			$folder = rtrim ( SELF::STANDARD_PATH ( realpath ( $dir ) ), '/' ) . '/';
			$parent = rtrim ( SELF::STANDARD_PATH ( realpath ( dirname ( $folder ) ) ), '/' ) . '/';
			
			// RETURN THE PARENT PATH AND CURRENT PATH
			$return = [ rawurlencode ( $parent ), $dir ];
		}
		
		// INCLUDE THE FILE COUNT IF INSTRUCTED TO COUNT FILES
		if ( $countFiles ) $return['count'] = $index;
		
		// RETURN THE RESULTS
		return $do ( $return );
	}
	
	/*///------------------------------------------------------------------------
			>>> CHECK IF FILE IS A SHORTCUT
	/*///------------------------------------------------------------------------
	FINAL PUBLIC STATIC FUNCTION IS_SHORTCUT
	(
			string $url	// FILE ADDRESS/LOCATION
	): BOOL
	{
		$ext = strtolower ( pathinfo ( $url, PATHINFO_EXTENSION ) );
		if ( !file_exists ( $url ) || $ext != 'lnk' ) return false;
		
		if ( is_link ( $url ) || SELF::IS_WINDOWS_LINK ( $url ) )
		{
			return true;
		}
		
		return false;
	}
	
	/*///------------------------------------------------------------------------
			>>> RETURN SHORTCUT TARGET LOCATION
	/*///------------------------------------------------------------------------
	FINAL PUBLIC STATIC FUNCTION READ_SHORTCUT
	(
			string $url	// FILE ADDRESS/LOCATION
	): STRING
	{
		$ext = strtolower ( pathinfo ( $url, PATHINFO_EXTENSION ) );
		if ( !file_exists ( $url ) || $ext != 'lnk' ) return '';
		
		if ( is_link ( $url ) )
		{
			return readlink ( $url );
		}
		else if ( SELF::IS_WINDOWS_LINK ( $url ) )
		{
			return SELF::READ_WINDOWS_LINK ( $url );
		}
		
		return '';
	}
	
	/*///------------------------------------------------------------------------
			>>> CHECK IF FILE IS A WINDOWS LINK/SHORTCUT
	/*///------------------------------------------------------------------------
	FINAL PUBLIC STATIC FUNCTION IS_WINDOWS_LINK
	(
			string $url	// FILE ADDRESS/LOCATION
	): BOOL
	{
		$ext = strtolower ( pathinfo ( $url, PATHINFO_EXTENSION ) );
		if ( !file_exists ( $url ) || $ext != 'lnk' ) return false;
		$bin = file_get_contents ( $url, 2048 );
		return substr ( $bin, 0, 20 ) ==	"L\000\000\000\001\024\002\000\000\000".
											"\000\000\300\000\000\000\000\000\000F";
	}
	
	/*///------------------------------------------------------------------------
			>>> READ WINDOWS LINK/SHORTCUT TARGET LOCATION
	/*///------------------------------------------------------------------------
	FINAL PUBLIC STATIC FUNCTION READ_WINDOWS_LINK
	(
			string $url	// FILE ADDRESS/LOCATION
	): STRING
	{
		$ext = strtolower ( pathinfo ( $url, PATHINFO_EXTENSION ) );
		if ( !file_exists ( $url ) || $ext != 'lnk' ) return '';
		return SELF::ARRAY_KEY ( SELF::LINK_INFO ( $url ), 'path', '' );
	}
	
	/*///------------------------------------------------------------------------
			>>> READ WINDOWS LINK/SHORTCUT INFO
	/*///------------------------------------------------------------------------
	FINAL PUBLIC STATIC FUNCTION LINK_INFO
	(
			string $url	// FILE ADDRESS/LOCATION
	): ARRAY
	{
		// taken from "The Windows Shortcut File Format.pdf" V1.0 as
		// reverse-engineered by Jesse Hager <jessehager@iname.com>
		if ( !file_exists ( $url ) ) return [];
		
		$bin = file_get_contents ( $url, 2048 );
		
		if (!defined("WIN_LNK_F_ITEMLIST"))
		{
			define("WIN_LNK_F_ITEMLIST", 1);
			define("WIN_LNK_F_FILE", 2);
			define("WIN_LNK_F_DESC", 4);
			define("WIN_LNK_F_RELATIVE", 8);
			define("WIN_LNK_F_WORKDIR", 16);
			define("WIN_LNK_F_CMDARGS", 32);
			define("WIN_LNK_F_ICON", 64);
			define("WIN_LNK_F2_DIR", 16);
			
			function bread(&$bin, &$p, $bytes=4)
			{
				$h = bin2hex( strrev($s = substr($bin, $p, $bytes)) );
				$v = base_convert($h, 16, 10);
				$p += $bytes;
				return $v;
			}
		}
		
		$res = array();
		$p = 0x14;
		$fl=$res["flags"] = bread($bin,$p);
		$res["t_attr"] = bread($bin,$p);
		$p = 0x4C;
		
		if ($fl & WIN_LNK_F_ITEMLIST)
		{
			$p += bread($bin,$p,2);
		}
		
		if ($fl & WIN_LNK_F_FILE)
		{
			$p0 = $p;
			$p = $p0 + 0x10;
			$p_path = $p0 + bread($bin,$p);
			$p = $p0 + 0x18;
			$p_file = $p0 + bread($bin,$p);
			$path = substr($bin, $p_path, 704);
			$path = substr($path, 0, strpos($path, "\000"));
			$file = substr($bin, $p_file, 704);
			$file = substr($file, 0, strpos($file, "\000"));
			$res["path"] = $path;
			$res["file"] = $file;
		}
		
		return $res;
	}
	
	/*///------------------------------------------------------------------------
			>>> FIND PROPER PATH TO FILE
	/*///------------------------------------------------------------------------
	FINAL PUBLIC STATIC FUNCTION FILEPATH 
	(
			string $url	// FILE ADDRESS/LOCATION
	)
	{
		$root = $_SERVER['DOCUMENT_ROOT'];
		$host =	( strpos ( $url, '/' ) !== false ) ? explode ( '/', SELF::STRIP_HTTP ( $url ) ) [0] : '';
		return	file_exists ( $url ) ? $url : (
				file_exists ( $new_url = SELF::STRIP_HTTP (str_ireplace($host, $root, $url)) )
				? $new_url : ( file_exists ( $new_url = "/$url" )
				? $new_url : ( file_exists ( $new_url = "$root/$url" )
				? $new_url : $url ) ) );
	}
	
	/*///------------------------------------------------------------------------
			>>> DECODE URLENCODED STRING
	/*///------------------------------------------------------------------------
	FINAL PUBLIC STATIC FUNCTION FILENAME_DECODE
	(
			string $filename	// FILE NAME/LOCATION
	): STRING
	{
		// STORE FILENAME TEMPORARILY
		$fn = $filename;
		
		// TEST THE PROPER ENCODING FOR THE FILENAME STRING
		$filename = is_file ( $filename ) ? $filename : rawurldecode ($fn);
		$filename = is_file ( $filename ) ? $filename : urldecode ( $fn );
		$filename = is_file ( $filename ) ? $filename : rawurldecode ( urldecode ( $fn ) );
		$filename = is_file ( $filename ) ? $filename : urldecode ( rawurldecode ( $fn ) );
		
		// RETURN DECODED FILENAME
		return $filename;
	}
	
	/*///------------------------------------------------------------------------
			>>> RE-ENCODE URLENCODED STRING
	/*///------------------------------------------------------------------------
	FINAL PUBLIC STATIC FUNCTION FILENAME_REENCODE
	(
			string $filename	// FILE NAME/LOCATION
	): STRING
	{
		// STRIP ALL URL ENCODING AND APPLY RAW URL ENCODING
		$filename = rawurlencode ( SELF::FILENAME_DECODE ( $filename ) );
		
		// RETURN DECODED FILENAME
		return $filename;
	}
	
	/*///------------------------------------------------------------------------
			>>> GET INFORMATION ABOUT THE FILE PATH/URL
	/*///------------------------------------------------------------------------
	FINAL PUBLIC STATIC FUNCTION LOCATION_INFO
	(
			string &$filename,		// FILE NAME/LOCATION
			string &$mimetype = '',	// FILE MIMETYPE
			string &$extension = ''	// FILE EXTENSION
	): ARRAY
	{
		// DECODE URLENCODED FILENAME
		$filename = SELF::FILENAME_DECODE ( $filename );
		
		// GET MIMETYPE
		$mimetype = is_file ( $filename ) ?
					(
						( $mimetype != '' ) ?
						$mimetype : strtolower ( mime_content_type ( $filename ) )
					) : '';
		
		// GET EXTENSION
		$extension =	( $extension != '' ) ?
						$extension : strtolower ( pathinfo ( $filename, PATHINFO_EXTENSION ) );
		
		// RETURN COLLECTED INFORMATION
		return	[
					'filename' => $filename,
					'mimetype' => $mimetype,
					'extension' => $extension
				];
	}
	
	/*///------------------------------------------------------------------------
			>>> CREATE NEW MEDIA STREAM
	/*///------------------------------------------------------------------------
	FINAL PUBLIC STATIC FUNCTION MEDIA 
	(
			string $location	// FILE ADDRESS/LOCATION
	)
	{
		return new MEDIA ( $location );
	}
	
	/*///------------------------------------------------------------------------
			>>> SET FILE-TYPE FOR FILE TO BE DISPLAYED
	/*///------------------------------------------------------------------------
	FINAL PUBLIC STATIC FUNCTION ECHO_URL_FILE
	(
			string $url,			// FILE ADDRESS/LOCATION
			bool $no_cache = false	// CACHE FILE?
	)
	{
		// CHECK IF URL IS REMOTE AND ACCESSIBLE
		$isremote = SELF::URLEXISTS ( $url );
		
		// CHECK IF URL IS LOCAL AND ACCESSIBLE
		$islocal = file_exists ($url);
		
		// EXTRACT FILENAME
		$filename = basename ( $url );
		
		// CONTROL CACHE
		if ( $no_cache ) header ( "Cache-Control: no-cache, must-revalidate" );
		
		// IF...
		if
		(
				// FILE IS LOCAL AND...
				!$isremote && $islocal &&
				
				// SIZE IS GREATER THAN 5MB
				( $size = intval ( sprintf ( "%u", filesize ( $url ) ) ) ) >
				( $chunks = 5 * ( 1024 * 1024 ) )
		)
		{
			$mime = mime_content_type ( $url );
			header ( "Content-Type: $mime" );
			header ( "Content-Length: $size" );
			header ( "Content-Disposition: attachment;filename=\"{$filename}\"" );
			$handle = fopen ( $url, 'rb' );
			while ( !feof ( $handle ) )
			{ 
				print ( @fread ( $handle, $chunks ) );
				ob_flush();
				flush();
			}
			fclose ( $handle ); 
		}
		// ELSE IF FILE IS LOCAL OR REMOTE
		else if ( $islocal || $isremote )
		{
			// READ DATA
			$data = file_get_contents($url);
			
			// GET MIME TYPE
			$mime = ( new finfo ( FILEINFO_MIME_TYPE ) )->buffer ( $data );
			
			//SET THE HEADERS
			header ("Content-Type: {$mime}");
			header ("Content-Disposition: FileName=\"{$filename}\"");
			
			// ECHO DATA
			echo $data;
		}
		
		// TERMINATE SCRIPT
		die;
	}
	
	/*///------------------------------------------------------------------------
			>>> SET FILE-TYPE FOR FILE TO BE DISPLAYED
	/*///------------------------------------------------------------------------
	FINAL PUBLIC STATIC FUNCTION SET_MIMETYPE 
	(
			string $data,								// FILE DATA
			string $filename = '',						// PREFERRED FILENAME
			int $info_requested = FILEINFO_MIME_TYPE	// HEADER INFO TO BE USED
	): STRING
	{
		// CREATE RANDOM FILENAME IF FILENAME IS NOT SET
		if ( $filename == '' )
		{
			$filename = time().rand(100000,999999);
		}
		
		// IF ONLY THE EXTENSION IS SET, CREATE A FILENAME AND USE THE EXTENSION
		if ( substr ( $filename, 0, 1 ) === '.')
		{
			$filename = time().rand(100000,999999) . $filename;
		}
		
		// GET THE INFORMATION REQUESTED
		$contentType = ( new finfo ( $info_requested ) )->buffer ( $data );
		
		//SET THE HEADERS
		header ("Content-Type: {$contentType}");
		header ("Content-Disposition: FileName=\"{$filename}\"");
		
		// RETURN THE CONTENT TYPE
		return $contentType;
	}
	
	/*///------------------------------------------------------------------------
			>>> SET FILE-TYPE FOR FILE RETRIEVED FROM URL
	/*///------------------------------------------------------------------------
	FINAL PUBLIC STATIC FUNCTION SET_URL_MIMETYPE
	(
			string $url,								// FILE ADDRESS/LOCATION
			string &$filename = '',						// PREFERRED FILENAME
			int $info_requested = FILEINFO_MIME_TYPE	// HEADER INFO TO BE USED
	)
	{
		// INITIALIZE CONTENT OF RETURN VALUE
		$data = false;
		
		try
		{
			// SET ENCODING FOR FOREIGN CHARACTERS
			$url = iconv ( 'utf-8', 'utf-8//IGNORE', $url );
			
			// READ THE DATA
			$data = file_get_contents($url);
			
			// SET REAL FILENAME
			if ( $filename == '' )
			{
				$filename = basename($url);
			}
			
			// SET MIME TYPE
			SELF::SET_MIMETYPE ( $data, $filename, $info_requested );
		}
		catch (Exception $error)
		{
			// RETURN FALSE AND SAVE AN ERROR MESSAGE IF FILE IS NOT FOUND
			SELF::$STATIC['ERROR'] = $error->getMessage();
			return false;
		}
		
		// RETURN DATA IF FILE IS FOUND
		return $data;
	}
	
	/*///------------------------------------------------------------------------
			>>> SET MIME TYPE AND ECHO BLOB RETRIEVED FROM DATABASE
	/*///------------------------------------------------------------------------
	FINAL PUBLIC STATIC FUNCTION ECHO_BLOB 
	(
			string $data,								// FILE DATA
			string $filename = '' ,						// PREFERRED FILENAME
			int $info_requested = FILEINFO_MIME_TYPE	// HEADER INFO TO BE USED
	)
	{
		SELF::SET_MIMETYPE($data, $filename, $info_requested);
		echo $data;
		die;
	}
	
	/*///------------------------------------------------------------------------
			>>> ATTEMPT TO OPEN ZIP FILE
	/*///------------------------------------------------------------------------
	FINAL PRIVATE STATIC FUNCTION TRY_ZIP_OPEN
	(
			string &$zipfile,			// ZIP FILE'S URL/LOCATION
			resource &$archive = NULL	// ZipArchive
	): BOOL
	{
		// CREATE A NEW ARCHIVE IF NONE IS SET
		if ( $archive === NULL )
		{
			$archive = new ZipArchive();
		}
		
		// OPEN ZIP FILE
		$result = $archive->open($zipfile);
		
		// CHECK IF FILE NOT FOUND
		if ( $result !== true && $result === ZipArchive::ER_NOENT )
		{
			// KEEP THE RAW PATH TEMPORARILY
			$raw = $zipfile;
			
			// TRY TO DECODE (RFC 3986) URL ENCODED CHARACTERS AND OPEN
			$zipfile = rawurldecode($raw);
			$result = $archive->open($zipfile);
			
			// CHECK IF FILE NOT FOUND
			if ( $result !== true && $result === ZipArchive::ER_NOENT )
			{
				// TRY TO DECODE URL ENCODED CHARACTERS AND OPEN
				$zipfile = urldecode($raw);
				$result = $archive->open($zipfile);
			}
		}
		
		// IF THE RESULT IS STILL A FAILURE, THROW AN ERROR
		if ( $result !== true )
		{
			SELF::$STATIC['ERROR'] = "Unable to open file '{$zipfile}'.";
			return false;
		}
		
		// RETURN THE REFERENCE TO THE OPENED ZIP FILE
		return $result;
	}
	
	/*///------------------------------------------------------------------------
			>>> ECHO REQUEST FROM ZIP FILE
	/*///------------------------------------------------------------------------
	FINAL PUBLIC STATIC FUNCTION ZIP_REQUEST
	(
			string $array_keys,				// LIST OF ARRAY KEYS TO SEARCH FROM REQUEST
			string $request_method = 'GET'	// OPTIONAL TYPE OF REQUEST EXPECTED
	)
	{
		// EXTRACT DATA FROM REQUEST
		$parameters = SELF::ARRAY_KEY_ARRAY ( $_REQUEST, $array_keys, '' );
		
		// VERIFY IF PARAMETERS ARE SUFFICIENT
		if ( SELF::MANDATORY_ARRAY_ELEMENTS ( $parameters, 2, '' ) )
		{
			// ECHO THE FILE
			SELF::ECHO_ZIP_CONTENT(...$parameters);
			die;
		}
	}
	
	/*///------------------------------------------------------------------------
			>>> ECHO THE CONTENT OF ZIP FILE
	/*///------------------------------------------------------------------------
	FINAL PUBLIC STATIC FUNCTION ECHO_ZIP_CONTENT
	(
			string $zip_file,							// ZIP FILE'S URL/LOCATION
			string $content_file,						// CONTENT TO BE EXTRACTED FROM ZIP FILE
			resource $archive = NULL,					// ZipArchive
			int $info_requested = FILEINFO_MIME_TYPE,	// HEADER INFO TO BE USED
			bool $no_cache = false,						// CACHE FILE?
			bool $allow404 = true						// ALLOW 404 ERROR MESSAGE?
	)
	{
		// SET CACHE CONTROL
		if ( $no_cache ) header ( "Cache-Control: no-cache, must-revalidate" );
		
		// MAKE SURE THAT THE ZIP FILE ENDS WITH A PROPER .ZIP EXTENSION
		$zip_file = str_ireplace ( '.zip', '', $zip_file ) . '.zip';
		
		// READ THE ZIP CONTENT
		$echo = SELF::READ_ZIP_CONTENT
		(
			SELF::STANDARD_PATH( $zip_file ),
			SELF::STANDARD_PATH( $content_file ), '',
			$archive,
			$info_requested
		);
		
		if ( $echo === false && $allow404 )
		{
			// SHOW ERROR 404
			SELF::BASE64_ECHO_AS_DATA ( FILES::error404 );
		}
		
		echo $echo;
		
		// END SCRIPT
		die;
	}
	
	/*///------------------------------------------------------------------------
			>>> READ THE CONTENT OF ZIP FILE
	/*///------------------------------------------------------------------------
	FINAL PUBLIC STATIC FUNCTION READ_ZIP_CONTENT
	(
			string $zipfile,							// ZIP FILE'S URL/LOCATION
			string $zipcontent,							// CONTENT TO BE EXTRACTED FROM ZIP FILE
			string $filename = '',						// PREFERRED FILENAME
			resource $archive = NULL,					// ZipArchive
			int $info_requested = FILEINFO_MIME_TYPE	// HEADER INFO TO BE USED
	)
	{
		// TRY TO OPEN THE ZIP FILE (THROW ERROR ON FAILURE)
		if ( SELF::TRY_ZIP_OPEN ( $zipfile, $archive ) )
		{
			// SET TO DEFAULT DIRECTORY IF ZIP FILE HAS NO DIRECTORY
			if ( count ( explode ( '/', str_replace ('\\', '/', $zipfile) ) ) == 1 )
			{
				$zipfile = getcwd().'/'.basename($zipfile);
			}
			
			// SET FILENAME
			if ( $filename == '' )
			{
				$filename = basename ( $zipcontent );
			}
			
			// CREATE ZIP PATH
			$path = "zip://{$zipfile}#{$zipcontent}";
			
			// CHECK IF FILE EXISTS IN ZIP ARCHIVE
			if ($archive->locateName($zipcontent) !== false)
			{
				// SET THE CONTENT TYPE
				$result = SELF::SET_URL_MIMETYPE ( $path, $filename, $info_requested );
				
				if ($result)
				{
					// CLOSE THE ARCHIVE AND RETURN THE DATA FOR FURTHER PROCESSING
					$archive->close();
					return $result;
				}
			}
		}
		
		// RETURN FALSE IF FAILED
		return false;
	}
	
	/*///------------------------------------------------------------------------
			>>> LIST THE CONTENT(S) OF ZIP FILE
	/*///------------------------------------------------------------------------
	FINAL PUBLIC STATIC FUNCTION LIST_ZIP_CONTENT
	(
			string $zipfile,			// ZIP FILE'S URL/LOCATION
			int $start = 1,				// STARTING INDEX
			int $end = 0,				// ENDING INDEX
			bool $detailed = false,		// RETURN DATA WITH DETAILS
			resource $archive = NULL	// ZipArchive
	): ARRAY
	{
		// PREPARE THE RETURN ARRAY
		$return = [];
		
		// INITIALIZE FILE COUNTER
		$filectr = 0;
		$count = 0;
		
		// TRY TO OPEN THE ZIP FILE (THROW ERROR ON FAILURE)
		if ( SELF::TRY_ZIP_OPEN($zipfile, $archive) )
		{
			// READ THE FILES IN THE ZIP ARCHIVE ONE BY ONE
			for( $index = 0; $index < $archive->numFiles; $index++ )
			{
				$file = $archive->statIndex( $index );
				
				// CHECK IF THE FILE IS A DIRECTORY
				if 
				(
					substr($file['name'], -1 ) != '/' &&
					substr($file['name'], -1 ) != '\\'
				)
				{
					// COUNT FILE NUMBER
					$filectr += 1;
					if ( $filectr < $start ) continue;
					if ( $end > 0 && $filectr > $end ) break;
					
					// ADD THE FILENAME TO THE RESULT ARRAY
					if ( $detailed )
					{
						$return['file'][] = $file['name'];
					}
					else
					{
						$return[] = $file['name'];
					}
					
					$count++;
				}
			}
			
		}
		
		// INCLUDE NUMBER OF FILES
		if ( $detailed )
		{
			if ( !array_key_exists ( 'file', $return ) ) $return['file'] = [];
			$return['count'] = $count;
		}
		
		// RETURN THE RESULT ARRAY
		return $return;
	}
	
	/*///------------------------------------------------------------------------
			>>> READ IMAGE FROM ZIP FILE
	/*///------------------------------------------------------------------------
	FINAL PUBLIC STATIC FUNCTION READ_ZIP_IMAGE
	(
			string $zipfile, // FILENAME OF ZIP ARCHIVE
			string $filename // FILENAME OF FILE WITHIN ZIP
	): BOOL
	{
		// CHECK IF URL IS VALID
		$raw = $zipfile;
		if ( !file_exists ( $zipfile ) ) { $zipfile = rawurldecode($raw); }
		if ( !file_exists ( $zipfile ) ) { $zipfile = urldecode($raw); }
		if ( !file_exists ( $zipfile ) ) { return false; }
		
		// START ZIP ARCHIVE
		$zip = new ZipArchive();
		if ( $zip->open( $zipfile ) !== true )
		{
			return false;
		}
		
		// GET IMAGE DATA
		$stat = $zip->statName($filename);
		$data = $zip->getStream($filename);
		
		if(!$data){ return false; }
		
		// SET HEADERS
		// header ( "Cache-Control: no-cache, must-revalidate" );
		header ( "Content-Type: image/jpeg" );
		header ( "Content-Disposition: FileName=\"{$filename}\"" );
		header ( "Content-Length: {$stat['size']}" );
		
		// EXECUTE
		fpassthru($data);
		
		// RETURN TRUE IF ALL IS SUCCESSFUL
		return true;
	}
	
	/*///------------------------------------------------------------------------
			>>> CONVERT FILE DATA FROM STATED LOCATION TO BASE 64
	/*///------------------------------------------------------------------------
	FINAL PUBLIC STATIC FUNCTION BASE64_FROM_LOCATION
	(
			string $location // FILE ADDRESS/LOCATION
	): STRING
	{
		return SELF::BASE64_FROM_DATA( file_get_contents ( $location ) );
	}
	
	/*///------------------------------------------------------------------------
			>>> CONVERT FILE DATA TO BASE 64
	/*///------------------------------------------------------------------------
	FINAL PUBLIC STATIC FUNCTION BASE64_FROM_DATA
	(
			string $data // FILE DATA
	): STRING
	{
		$type = ( new finfo ( FILEINFO_MIME_TYPE ) )->buffer( $data );
		return 'data:' . $type . ';base64,' . base64_encode( $data );
	}
	
	/*///------------------------------------------------------------------------
			>>> APPLY SPECIAL FORMATTING TO FILE
	/*///------------------------------------------------------------------------
	FINAL PUBLIC STATIC FUNCTION BASE64_FORMAT_APPLY
	(
			string $location,			// FILE ADDRESS/LOCATION
			string $beginning = '',		// OPTIONAL STRING TO INSERT AT THE BEGINNING OF EACH LINE
			string $ending = '',		// OPTIONAL STRING TO INSERT AT THE END OF EACH LINE
			string $separator = "\r\n"	// OPTIONAL CHARACTER/STRING TO USE AS SEPARATOR
	): STRING
	{
		// EXTRACT DATA FROM LOCATION
		$data = file_get_contents ( $location );
		
		// EXTRACT MIME TYPE FROM DATA
		$MIME = ( new finfo ( FILEINFO_MIME_TYPE ) )->buffer( $data );
		
		// EXTRACT FILENAME FROM DATA
		$filename = basename ( $location );
		
		// RETURN IN SPECIAL FORMAT
		return	"{$beginning}data:{$MIME};" .
				"filename={$filename};" .
				"base64,{$ending}{$separator}" .
				SELF::SPLIT_BY_LENGTH
				(
					base64_encode ( $data ),
					80,
					$separator,
					$beginning,
					$ending
				);
	}
	
	/*///------------------------------------------------------------------------
			>>> STRIP SPECIAL FORMATTING APPLIED TO BASE64 STRING
	/*///------------------------------------------------------------------------
	FINAL PUBLIC STATIC FUNCTION BASE64_FORMAT_STRIP
	(
			string $base64_data,	// DATA TO BE PROCESSED
			string &$filename = ''	// OPTIONAL REFERENCE TO STORE FILENAME
	): STRING
	{
		// SEPARATE CONTENTS
		$base64_array = explode ( ';', $base64_data );
		
		// EXTRACT FILENAME FROM STRING
		$filename = str_replace ( 'filename=', '', $base64_array[1] );
		
		// STRIP SECIAL FORMAT
		return str_replace
		(
			"{$base64_array[0]};{$base64_array[1]};base64,", '',
			str_replace ( "\r\n", '', $base64_data )
		);
	}
	
	/*///------------------------------------------------------------------------
			>>> APPLY FORMATTING THAT CAN BE USED BY JAVASCRIPT TO FILE
	/*///------------------------------------------------------------------------
	FINAL PUBLIC STATIC FUNCTION BASE64_FILE_TO_JAVASCRIPT
	(
			string $location,		// FILE ADDRESS/LOCATION
			int $number_of_tabs = 0	// NUMBER OF INDENTATION TABS
	): STRING
	{
		// CREATE A STRING OF TABS
		$tabs = SELF::STRING_OF("\t", $number_of_tabs);
		
		// FORMAT THE FILE DATA
		return SELF::BASE64_FORMAT_APPLY ( $location, "'", "'", " +\r\n{$tabs}" );
	}
	
	/*///------------------------------------------------------------------------
			>>> APPLY FORMATTING THAT CAN BE USED BY JAVASCRIPT TO RESOURCE
	/*///------------------------------------------------------------------------
	FINAL PUBLIC STATIC FUNCTION BASE64_RESOURCE_TO_JAVASCRIPT
	(
			string $resourceName,			// NAME OF CONSTANT
			int $number_of_tabs = 0,		// NUMBER OF INDENTATION TABS
			string $resourceClass = 'FILES'	// CLASS CONTAINING THE RESOURCE
	)
	{
		// CREATE A STRING OF TABS
		$tabs = SELF::STRING_OF("\t", $number_of_tabs);
		
		// FORMAT THE RESOURCE DATA
		return "'" .
		str_replace
		(
			"\r\n", "' +\r\n{$tabs}'",
			str_replace
			(
				"\t", '',
				trim ( constant( "{$resourceClass}::{$resourceName}" ) )
			)
		) . "'";
	}
	
	/*///------------------------------------------------------------------------
			>>> STRIP SPECIAL FORMATTING APPLIED TO BASE64 STRING
	/*///------------------------------------------------------------------------
	FINAL PUBLIC STATIC FUNCTION BASE64_ECHO_AS_DATA
	(
			string $base64_data,	// DATA TO BE PROCESSED
			string $filename = ''	// PREFERRED FILENAME
	)
	{
		// TEMPORARILY STORE PREFERRED FILENAME
		$temp = $filename;
		
		// STRIP THE FORMATTING, RETRIEVE THE FILENAME AND DECODE THE BASE64 STRING
		$data = base64_decode ( SELF::BASE64_FORMAT_STRIP( $base64_data, $filename ) );
		
		// IF THE PREFERRED FILENAME IS NOT EMPTY, RESTORE AND USE THAT FILENAME
		if ( $temp != '' )
		{
			$filename = $temp;
		}
		
		// SET THE HEADERS
		SELF::SET_MIMETYPE ( $data, $filename );
		
		// ECHO THE DATA
		echo ( $data );
	}
	
	/*///------------------------------------------------------------------------
			>>> ECHO DATA FROM RESOURCE
	/*///------------------------------------------------------------------------
	FINAL PUBLIC STATIC FUNCTION ECHO_RESOURCE
	(
			string $resourceName,				// NAME OF CONSTANT
			string $filename = '',				// PREFERRED FILENAME
			string $resourceClass = 'FILES',	// CLASS CONTAINING THE RESOURCE
			bool $allow404 = true				// ALLOW 404 ERROR MESSAGE?
	): BOOL
	{
		$constant = "{$resourceClass}::{$resourceName}";
		
		// CHECK IF RESOURCE CONSTANT NAME IS EMPTY
		if ( $resourceName == '' || $resourceName == NULL )
		{
			if ( $allow404 )
			{
				// IF CONSTANT IS NOT FOUND, SHOW ERROR 404
				SELF::BASE64_ECHO_AS_DATA ( constant( "{$resourceClass}::error404" ), $filename );
				
				// END THE SCRIPT
				die;
			}
			
			// THROW AN ERROR AND RETURN FALSE
			SELF::$STATIC['ERROR'] = "Resource not specified.";
			return false;
		}
		
		// CHECK IF CONSTANT IS DEFINED
		if ( defined ( $constant ) )
		{
			// IF CONSTANT IS DEFINED, CONVERT THE STRING TO DATA AND ECHO
			SELF::BASE64_ECHO_AS_DATA ( constant( $constant ), $filename );
			
			// END THE SCRIPT
			die;
		}
		else if ( $allow404 )
		{
			// IF CONSTANT IS NOT FOUND, SHOW ERROR 404
			SELF::BASE64_ECHO_AS_DATA ( constant( "{$resourceClass}::error404" ), $filename );
			
			// END THE SCRIPT
			die;
		}
		
		// THROW AN ERROR AND RETURN FALSE
		SELF::$STATIC['ERROR'] = "Unable to find '{$resourceName}'.";
		return false;
	}
	
	/*///------------------------------------------------------------------------
			>>> RESIZE IMAGE FROM RESOURCE
	/*///------------------------------------------------------------------------
	FINAL PUBLIC STATIC FUNCTION IMAGE_RESIZE_RESOURCE
	(
			string $resourceName = 'FILES::error404',	// FILE DATA/BLOB
			string $lock = 'auto',						// RESIZE BASED ON HEIGHT / WIDTH / AUTO
			int $max_size = 130,						// SET MAX SIZE OF HEIGHT / WIDTH
			bool $no_cache = false,						// CACHE IMAGE?
			bool $blur = false,							// BLUR IMAGE DURING RESIZE?
			bool $resampled = true,						// USE RESAMPLING? (CAN BE SLOWER)
			bool $simple = false						// USE SIMPLE RESIZE?
	)
	{
		if ( strpos ( $resourceName, '::' ) === false )
		{
			$resourceName = "FILES::$resourceName";
		}
		else if ( count ( SELF::DISASSEMBLE ( $resourceName, '::' ) ) !== 2 )
		{
			return SELF::ERROR;
		}
		
		if ( !defined ( $resourceName ) ) return SELF::ERROR;
		$filename = '';
		$data = base64_decode ( SELF::BASE64_FORMAT_STRIP ( constant( $resourceName ) ), $filename );
		SELF::SET_MIMETYPE ( $data, $filename );
		SELF::IMAGE_RESIZE ( $data, $lock, $max_size, '', $no_cache, $blur, $resampled, $simple );
	}
	
	/*///------------------------------------------------------------------------
			>>> RESIZE IMAGE FROM DATA/BLOB
	/*///------------------------------------------------------------------------
	FINAL PUBLIC STATIC FUNCTION IMAGE_RESIZE_DATA
	(
			string $data,			// FILE DATA/BLOB
			string $filename = '',	// PREFERRED FILENAME
			string $lock = 'auto',	// RESIZE BASED ON HEIGHT / WIDTH / AUTO
