	/*///------------------------------------------------------------------------
			>>> RESIZE IMAGE FROM IMAGE DATA
	/*///------------------------------------------------------------------------
	FINAL PUBLIC STATIC FUNCTION IMAGE_RESIZE
	(
			string $data,			// IMAGE DATA
			string $lock = 'auto',	// RESIZE BASED ON HEIGHT / WIDTH / AUTO
			int $max_size = 130,	// SET MAX SIZE OF HEIGHT / WIDTH
			string $file = '',		// FILE LOCATION IF AVAILABLE
			bool $no_cache = false,	// DISABLE IMAGE CACHE?
			bool $blur = false,		// BLUR IMAGE DURING RESIZE?
			bool $resampled = true,	// USE RESAMPLING FOR SMOOTHER RESIZED IMAGE? (CAN BE SLOWER)
			bool $simple = false	// USE SIMPLE RESIZE
	)
	{
		// CHECK IF FILE EXISTS
		$file_exists = $file !== '' && file_exists ( $file );
		
		// PREPARE MIME
		$mime = '';
		
		// ENSURE THAT RESIZE MODE IS LOWERCASE
		$lock = strtolower ( $lock );
		
		// CHECK IF RESIZE MODE IS VALID
		if ( !( $lock == 'width' || $lock == 'height' || $lock == 'auto' ) )
		{
			// SET TO DEFAULT IF NOT
			$lock = 'auto';
		}
		
		// INITIALIZE VARIABLES
		$image = NULL;
		$width = 0;
		$height = 0;
		
		if ( $file_exists )
		{
			// CHECK TYPE OF IMAGE
			$mime = mime_content_type ( $file );
			
			// READ IMAGE SIZE
			list($width, $height) = getimagesize ( $file );
			
			// CREATE IMAGE LAYER FROM SOURCE FILE
			if ( stripos ( $mime, 'wbmp' ) !== FALSE )
			{
				$image = imagecreatefromwbmp ( $file );
			}
			else if ( stripos ( $mime, 'bmp' ) !== FALSE )
			{
				SELF::BMPFUNCTIONS();
				$image = imagecreatefrombmp ( $file );
			}
			else if ( stripos ( $mime, 'png' ) !== FALSE )
			{
				$image = imagecreatefrompng ( $file );
			}
			else if ( stripos ( $mime, 'gif' ) !== FALSE )
			{
				$image = imagecreatefromgif ( $file );
			}
			else if
			(
					stripos ( $mime, 'jpg' ) !== FALSE ||
					stripos ( $mime, 'jpeg' ) !== FALSE
			)
			{
				$image = imagecreatefromjpeg ( $file );
			}
			else
			{
				header ( "Content-Type: text/html" );
				echo "Unknown mime type '{$mime}'.";
				die;
			}
		}
		else
		{
			// CHECK TYPE OF IMAGE
			$mime = strtolower( ( new finfo ( FILEINFO_MIME_TYPE ) )->buffer ( $data ) );
			
			// CREATE IMAGE
			$image = imagecreatefromstring ( $data );
			
			// READ IMAGE SIZE
			$width = imagesx ( $image );
			$height = imagesy ( $image );
		}
		
		// SETUP SCALE
		$scale = 1;
		if ( $lock == 'width' || ( $lock == 'auto' && $width >= $height ) )
		{
			// COMPUTE THE SCALE
			$scale = $max_size / $width;
		}
		else if ( $lock == 'height' || ( $lock == 'auto' && $width < $height ) )
		{
			// COMPUTE THE SCALE
			$scale = $max_size / $height;
		}
		
		// SETUP NEW IMAGE DIMENSIONS
		$newwidth = $width * $scale;
		$newheight = $height * $scale;
		
		// SWITCH ON ANTIALIAS
		if ( $blur ) imageantialias ( $image, true );
		
		// WHEN DOWNSCALING
		if ( $blur && $scale < 1 )
		{
			// CORRECT THE PIXEL SCALE
			imagefilter ( $image, IMG_FILTER_PIXELATE, ( 1 / $scale ), true );
			
			// BLUR FOR SMOOTHER OUTPUT / ANTIALIAS EFFECT WHEN RESIZED
			imagefilter ( $image, IMG_FILTER_GAUSSIAN_BLUR );
			
			// BLUR FURTHER FOR ANTIALIAS EFFECT
			//imagefilter ( $image, IMG_FILTER_SMOOTH, 1 - round ( 1 / $scale ) );
		}
		
		// USE A SIMPLE IMAGE SCALING METHOD ( LESS PRONE TO ERRORS )
		if ( $simple )
		{
			// CHANGE IMAGE DIMENSIONS
			$image = imagescale ( $image, $newwidth, $newheight );
		}
		else
		{
			// CREATE IMAGE CANVAS
			$newImage= imagecreatetruecolor ( $newwidth, $newheight );
			
			// SETUP IMAGE OPACITY
			imagesavealpha ( $newImage, true );
			
			// SETUP ALPHA
			$alpha = imagecolorallocatealpha ( $newImage, 0, 0, 0, 127 );
			
			// FILL IMAGE WITH ALPHA
			imagefill ( $newImage, 0, 0, $alpha );
			
			// SWITCH ON ANTIALIAS
			if ( $blur ) imageantialias ( $newImage, true );
			
			// PREPARE PARAMETERS
			$imgpars = [ $newImage, $image, 0, 0, 0, 0, $newwidth, $newheight, $width, $height ];
			
			// RESIZE IMAGE TO FIT CANVAS
			if ( $resampled )
			{
				imagecopyresampled ( ...$imgpars );
			}
			else
			{
				imagecopyresized ( ...$imgpars );
			}
			
			// REPLACE THE CURRENT IMAGE
			$image = $newImage;
		}
		
		if ( $blur && $scale < 1 )
		{
			// BLUR FOR SMOOTHER IMAGE
			imagefilter ( $image, IMG_FILTER_SELECTIVE_BLUR );
		}
		else if ( $blur )
		{
			imagefilter ( $image, IMG_FILTER_SELECTIVE_BLUR );
			imagefilter ( $image, IMG_FILTER_SMOOTH, 9 );
		}
		
		// DECLARE HEADERS OF OUTPUT IMAGE
		if ( $no_cache ) header ( "Cache-Control: no-cache, must-revalidate" );
		
		$parameters = [ $image ];
		
		// ECHO ACCORDING TO TYPE ( DEFAULT TO JPEG )
		if ( stripos ( $mime, 'wbmp' ) !== FALSE )
		{
			imagewbmp( ...$parameters );
		}
		else if ( stripos ( $mime, 'bmp' ) !== FALSE )
		{
			imagebmp( ...$parameters );
		}
		else if ( stripos ( $mime, 'png' ) !== FALSE )
		{
			imagepng( ...$parameters );
		}
		else if ( stripos ( $mime, 'gif' ) !== FALSE )
		{
			imagegif( ...$parameters );
		}
		else
		{
			imagejpeg( ...$parameters );
		}
		
		// FREE MEMORY
		imagedestroy($image);
		die;
	}
	
	/*///------------------------------------------------------------------------
			>>> LOAD BITMAP FUNCTIONS IF NONE FOUND
	/*///------------------------------------------------------------------------
	FINAL PUBLIC STATIC FUNCTION BMPFUNCTIONS()
	{
		if ( !function_exists ( 'imagebmp' ) )
		{
			function imagebmp
			(
					&$image_data,		// IMAGE DATA
					$savefile = false	// SAVE IMAGE AS FILE
			)
			{
				// SETUP DIMENSIONS AND PADDING
				$width		= imagesx ( $image_data );
				$height		= imagesy ( $image_data );
				$xpadding	= str_pad ( '', $width % 4, "\0" );
				
				// CALCULATE FILE SIZE
				$size = 54 + ( $width + strlen ( $xpadding ) ) * $height;
				
				// SETUP HEADER ( IN ORDER )
				$header['identifier']		= 'BM';
				$header['file_size']		= pack("V", $size);
				$header['reserved']			= pack("V", 0);
				$header['bitmap_data']		= pack("V", 54);
				$header['header_size']		= pack("V", 40);
				$header['width']			= pack("V", $width);
				$header['height']			= pack("V", $height);
				$header['planes']			= pack("v", 1);
				$header['bits_per_pixel']	= pack("v", 24);
				$header['compression']		= pack("V", 0);
				$header['data_size']		= pack("V", 0);
				$header['h_resolution']		= pack("V", 0);
				$header['v_resolution']		= pack("V", 0);
				$header['colors']			= pack("V", 0);
				$header['important_colors']	= pack("V", 0);
				
				// IF SAVE FILE
				if ( $savefile )
				{
					// CREATE A NEW FILE
					$file = fopen ( $savefile, "wb" );
					
					// WRITE THE HEADER
					foreach ( $header as $data )
					{
						fwrite ( $file, $data );
					}
					
					// SAVE TO FILE LINE BY LINE
					for ( $y = $height - 1; $y >= 0; $y--)
					{
						// SAVE TO LINE PIXEL BY PIXEL
						for ( $x = 0; $x < $width; $x++ )
						{
							// COPY PIXEL BY PIXEL FROM IMAGE
							$rgb = imagecolorat ( $image_data, $x, $y );
							
							// WRITE PER PIXEL TO FILE
							fwrite	(
										$file,
										chr ( $rgb & 255 ) .
										chr ( ( $rgb >> 8 ) & 255 ) .
										chr ( ( $rgb >> 16 ) & 255 )
									);
						}
						
						// WRITE NEW PADDING
						fwrite ( $file, $xpadding );
					}
					
					// CLOSE NEW FILE
					fclose ( $file );
				}
				else
				{
					// OUTPUT HEADERS
					foreach ( $header as $data ) { echo $data; }
					
					// OUTPUT IMAGE LINE BY LINE
					for ( $y = $height - 1; $y >= 0; $y-- )
					{
						// OUTPUT IMAGE PIXEL BY PIXEL
						for ( $x=0; $x < $width; $x++ )
						{
							// COPY PIXEL BY PIXEL FROM IMAGE
							$rgb = imagecolorat ( $image_data, $x, $y );
							
							// ECHO PER PIXEL
							echo	chr ( $rgb & 255 ) .
									chr ( ( $rgb >> 8 ) & 255 ) .
									chr ( ( $rgb >> 16 ) & 255 );
						}
						
						// ECHO NEW PADDING
						echo $xpadding;
					}
				}
			}
		}
		
		if ( !function_exists ( 'imagecreatefrombmp' ) )
		{
			function imagecreatefrombmp
			(
					$file // FILE LOCATION
			)
			{
				// LOAD THE IMAGE INTO A STRING
				$file = fopen ( $file, "rb" );
				$read = fread ( $file, 10 );
				while ( !feof ( $file ) && ( $read <> "" ) ) $read .= fread ( $file, 1024 );
				$temp = unpack ( "H*", $read );
				$hex = $temp [ 1 ];
				$header = substr ( $hex, 0, 108 );
				// PROCESS THE HEADER
				if ( substr ( $header, 0, 4 ) == "424d" )
				{
					// CUT IT IN PARTS OF	2 BYTES
					$header_parts	=	str_split ( $header, 2 );
					
					// GET THE WIDTH		4 BYTES
					$width			=	hexdec ( $header_parts [ 19 ] . $header_parts [ 18 ] );
					
					// GET THE HEIGHT		4 BYTES
					$height			=	hexdec ( $header_parts [ 23 ] . $header_parts [ 22 ] );
					
					// UNSET THE HEADER PARAMETERS
					unset ( $header_parts );
				}
				// DEFINE STARTING X AND Y
				$x = 0;
				$y = 1;

				// CREATE NEW IMAGE
				$image = imagecreatetruecolor ( $width, $height );

				// GRAB THE BODY FROM THE IMAGE
				$body = substr( $hex , 108 );

				// CALCULATE IF PADDING AT THE END-LINE IS NEEDED
				// DIVIDED BY TWO TO KEEP OVERVIEW.
				// 1 BYTE = 2 HEX-CHARS
				$body_size		= ( strlen ( $body ) / 2 );
				$header_size	= ( $width * $height );

				// USE END-LINE PADDING? ONLY WHEN NEEDED
				$usePadding		=	( $body_size > ( $header_size * 3 ) + 4 );

				// USING A FOR-LOOP WITH INDEX-CALCULATION INSTEAD
				// OF STR_SPLIT TO AVOID LARGE MEMORY CONSUMPTION
				// CALCULATE THE NEXT DWORD-POSITION IN THE BODY
				for ( $i = 0; $i < $body_size; $i += 3 )
				{
					// CALCULATE LINE-ENDING AND PADDING
					if ( $x >= $width )
					{
						// IF PADDING NEEDED, IGNORE IMAGE-PADDING
						// SHIFT I TO THE ENDING OF THE CURRENT 32-BIT-BLOCK
						if ( $usePadding ) $i += $width % 4;
						
						// RESET HORIZONTAL POSITION
						$x = 0;
						
						// RAISE THE HEIGHT-POSITION (BOTTOM-UP)
						$y++;
						
						// REACHED THE IMAGE-HEIGHT? BREAK THE FOR-LOOP
						if ( $y > $height ) break;
					}
					
					// CALCULATION OF THE RGB-PIXEL (DEFINED AS BGR IN IMAGE-DATA)
					// DEFINE $I_POS AS ABSOLUTE POSITION IN THE BODY
					$i_pos	= $i * 2;
					$r		= hexdec ( $body [ $i_pos + 4 ] . $body [ $i_pos + 5 ] );
					$g		= hexdec ( $body [ $i_pos + 2 ] . $body [ $i_pos + 3 ] );
					$b		= hexdec ( $body [ $i_pos + 0 ] . $body [ $i_pos + 1 ] );
					
					// CALCULATE AND DRAW THE PIXEL
					$color	= imagecolorallocate ( $image, $r, $g, $b );
					imagesetpixel ( $image, $x, ( $height - $y ), $color );
					
					// RAISE THE HORIZONTAL POSITION
					$x++;
				}
				
				// UNSET THE BODY / FREE THE MEMORY
				unset ( $body );
				
				// RETURN IMAGE-OBJECT
				return $image;
			}
		}
	}
	
	/*///------------------------------------------------------------------------
			>>> CREATE THUMBNAILS
	/*///------------------------------------------------------------------------
	FINAL PUBLIC STATIC FUNCTION THUMBNAILS
	(
			string $file,			// PATH TO FILE
			string $lock = 'auto',	// RESIZE BASED ON HEIGHT / WIDTH / AUTO
			int $max_size = 130,	// SET MAX SIZE OF HEIGHT / WIDTH
			bool $save = false		// SAVE THUMBNAIL?
	): BOOL
	{
		// ENSURE THAT RESIZE MODE IS LOWERCASE
		$lock = strtolower ( $lock );
		
		// CHECK IF RESIZE MODE IS VALID
		if ( !( $lock == 'width' || $lock == 'height' || $lock == 'auto' ) )
		{
			return false;
		}
		
		// CHECK IF PATH IS VALID
		if ( !file_exists ( $file ) )
		{
			// STORE FILE LOCATION TEMPORARILY
			$raw = $file;
			
			// DECODE URL
			$file = rawurldecode ( $raw );
			
			// CHECK IF PATH IS VALID
			if ( !file_exists ( $file ) )
			{
				// DECODE URL USING ANOTHER METHOD
				$file = urldecode ( $raw );
				
				// CHECK IF PATH IS VALID
				if ( !file_exists ( $file ) )
				{
					// RETURN FALSE IF FAILED
					return false;
				}
			}
		}
		
		// READ IMAGE TYPE
		$imageType = exif_imagetype ( $file );
		
		// GET THE IMAGE DIRECTORY
		$dir = dirname ( $file );
		
		// ASSUME THUMBS DIRECTORY
		$thumbs = $dir.'/rescale['.$lock.'_'.$max_size.'px]/';
		
		// GET FILE NAME
		$filename = basename ( $file );
		
		// ASSUME THUMBNAIL
		$thumbfile = $thumbs.$filename;
		
		// CREATE DIRECTORY
		if ( !file_exists($thumbs) && $save ) { mkdir ( $thumbs, 0777 ); }
		
		// DECLARE HEADERS OF OUTPUT IMAGE
		// header ( "Cache-Control: no-cache, must-revalidate" );
		SELF::SET_URL_MIMETYPE ( $file );
		
		// CHECK EXISTENCE OF THUMBNAIL
		if ( file_exists ( $thumbfile ) )
		{
			// READ THUMBS
			readfile ( $thumbfile );
		}
		else
		{
			// READ IMAGE SIZE
			list($width, $height) = getimagesize ( $file );
			
			// SETUP SCALE
			$scale = 1;
			if ( $lock == 'width' || ( $lock == 'auto' && $width >= $height ) )
			{
				$scale = $max_size / $width;
			}
			else if ( $lock == 'height' || ( $lock == 'auto' && $width < $height ) )
			{
				$scale = $max_size / $height;
			}
			
			// SETUP NEW IMAGE DIMENSIONS
			$newwidth = $width * $scale;
			$newheight = $height * $scale;
			
			// CREATE IMAGE CANVAS
			$newImage= imagecreatetruecolor ( $newwidth, $newheight );
			
			// SETUP IMAGE OPACITY
			imagesavealpha($newImage, true); //SWITCH ON ALPHA OPACITY
			$alpha = imagecolorallocatealpha ( $newImage, 0, 0, 0, 127 ); //SETUP ALPHA
			imagefill ( $newImage, 0, 0, $alpha ); //FILL IMAGE WITH ALPHA
			
			// SWITCH ON ANTIALIAS
			imageantialias ( $newImage, true );
			
			// CREATE IMAGE LAYER FROM SOURCE FILE
			if ( $imageType == IMAGETYPE_JPEG )
			{
				$source = imagecreatefromjpeg ( $file );
			}
			else if ( $imageType == IMAGETYPE_PNG )
			{
				$source = imagecreatefrompng ( $file );
			}
			else if ( $imageType == IMAGETYPE_GIF )
			{
				$source = imagecreatefromgif ( $file );
			}
			
			// RESIZE IMAGE TO FIT CANVAS
			imagecopyresized
				(
					$newImage, $source, 0, 0, 0, 0,
					$newwidth, $newheight, $width, $height
				);
			
			// OUTPUT IMAGE
			if ( $save )
			{
				// SAVE AND READ THUMBS
				if ( $imageType == IMAGETYPE_JPEG )
				{
					imagejpeg ( $newImage, $thumbfile );
				}
				else if ($imageType == IMAGETYPE_PNG)
				{
					imagepng ( $newImage, $thumbfile );
				}
				else if ($imageType == IMAGETYPE_GIF)
				{
					imagegif ( $newImage, $thumbfile );
				}
				
				readfile($thumbfile);
			}
			else
			{	// READ TEMPORARY FILE
				if ( $imageType == IMAGETYPE_JPEG )
				{
					imagejpeg ( $newImage );
				}
				else if ( $imageType == IMAGETYPE_PNG )
				{
					imagepng ( $newImage );
				}
				else if ( $imageType == IMAGETYPE_GIF )
				{
					imagegif ( $newImage );
				}
			}
			
			// CLEAR MEMORY
			imagedestroy ( $newImage );
		}
		
		// RETURN TRUE IF SUCCESSFUL
		return true;
	}
	
	
	/*///------------------------------------------------------------------------
	-----------------------------------------------------------------------------
*		DATABASE OPERATIONS
	-----------------------------------------------------------------------------
	/*///------------------------------------------------------------------------
	
	
	/*///------------------------------------------------------------------------
			>>> CONNECT TO DATABASE USING PDO
	/*///------------------------------------------------------------------------
	FINAL PUBLIC FUNCTION PDO_CONNECT
	(
			string $username = '',	// DATABASE USERNAME
			string $password = '',	// DATABASE PASSWORD
			string $host = '',		// DATABASE HOST
			string $database = ''	// DATABASE TO ACCESS
	)
	{
		// SET TO DEFAULT IF EMPTY
		if ($host == '') $host = $this->VARIABLES['HOST'];
		if ($database == '') $database = $this->VARIABLES['DATABASE'];
		if ($username == '') $username = $this->VARIABLES['USERNAME'];
		if ($password == '') $password = $this->VARIABLES['PASSWORD'];
		
		// SET VALUES FOR DEFAULT IF EMPTY
		if ($this->VARIABLES['HOST'] == '') $this->VARIABLES['HOST'] = $host;
		if ($this->VARIABLES['DATABASE'] == '') $this->VARIABLES['DATABASE'] = $database;
		if ($this->VARIABLES['USERNAME'] == '') $this->VARIABLES['USERNAME'] = $username;
		if ($this->VARIABLES['PASSWORD'] == '') $this->VARIABLES['PASSWORD'] = $password;
		
		try
		{
			// PREPARE DATABASE NAME
			$database = ( $database != '' ? 'dbname='.$database.';' : '' );
			
			// PREPARE STRING FOR DATABASE
			$database_string = "mysql:host={$host};{$database}";
			
			// CREATE CONNECTION
			$this->VARIABLES['CONNECTION'] = new PDO($database_string, $username, $password);
		}
		catch (PDOException $error)
		{
			// SET ERROR MESSAGE IF CONNECTION FAILED
			SELF::$STATIC['ERROR'] = $error->getMessage();
		}
		
		// RETURN THE CONNECTION
		return $this->VARIABLES['CONNECTION'];
	}
	
	/*///------------------------------------------------------------------------
			>>> CONNECT TO DATABASE TABLE USING PDO
	/*///------------------------------------------------------------------------
	FINAL PUBLIC FUNCTION PDO_DATABASE
	(
			string $database = '' // DATABASE TO CONNECT TO
	): BOOL
	{
		// SET TO DEFAULT IF EMPTY
		if ($database == '') $database = $this->VARIABLES['DATABASE'];
		
		// SET DATABASE IF DEFAULT IS EMPTY
		if ($this->VARIABLES['DATABASE'] == '') $this->VARIABLES['DATABASE'] = $database;
		
		// PREPARE RESULT
		$result = false;
		
		try
		{
			// TRY TO CONNECT TO DATABASE
			switch ( $this->VARIABLES['CONNECTION']->exec("USE {$database}") === false )
			{
				case true:
					// SET ERROR MESSAGE IF CONNECTION FAILED
					SELF::$STATIC['ERROR'] = "Failed to connect to '{$database}' database.";
					break;
					
				default:
					// SUCCESSFUL CONNECTION
					$result = true;
			}
		}
		catch (PDOException $error)
		{
			// SET ERROR MESSAGE IF CONNECTION FAILED
			SELF::$STATIC['ERROR'] = $error->getMessage();
			
			// RETURN FALSE IF CONNECTION ATTEMPT FAILED
			return false;
		}
		
		// RETURN TRUE IF CONNECTION IS SUCCESSFUL
		return $result;
	}
	
	/*///------------------------------------------------------------------------
			>>> DISCONNECT FROM DATABASE
	/*///------------------------------------------------------------------------
	FINAL PUBLIC FUNCTION PDO_DISCONNECT
	(
			resource &$connection = NULL // DATABASE CONNECTION
	)
	{
		$connection = NULL;
		$this->VARIABLES['CONNECTION'] = NULL;
	}
	
	/*///------------------------------------------------------------------------
			>>> BUILD PARAMETERS
	/*///------------------------------------------------------------------------
	FINAL PUBLIC FUNCTION PDO_PARAMETERS
	(
			array $parameters,	// ARRAY CONTAINING THE PARAMETERS
			array $skip = []	// ARRAY CONTAINING THE INDICES TO SKIP
	): ARRAY
	{
		// SET SKIP TO DEFAULT IF EMPTY
		if ( empty ( $skip ) ) { $skip = $this->VARIABLES['SKIP']; }
		
		// ADD THE LIST OF KEYS THAT ARE SKIPPED ONCE
		foreach ( $this->VARIABLES['SKIP_ONCE'] as $skipOnce )
		{
			if ( !in_array ( $skipOnce, $skip ) )
			{
				$skip[] = $skipOnce;
			}
		}
		
		// EMPTY THE LIST OF KEYS THAT ARE SKIPPED ONCE
		$this->VARIABLES['SKIP_ONCE'] = [];
		
		// PREPARE THE RETURN VALUE
		$return = [];
		
		// READ PARAMETERS
		foreach($parameters as $array_key=>$value)
		{
			// CHECK IF KEY SHOULD BE SKIPPED
			if ( !in_array($array_key, $skip) )
			{
				// CHECK IF THE CURRENT KEY IS AN ARRAY
				if (!is_array($value))
				{
					$return[":{$array_key}"] = $value;
				}
				else foreach($value as $inner_array_key=>$inner_value)
				{
					$return[":{$array_key}{$inner_array_key}"] = $inner_value;
				}
			}
		}
		
		// RETURN THE ARRAY TO BE USED FOR PDO QUERY
		return $return;
	}
	
	/*///------------------------------------------------------------------------
			>>> PDO QUERY EXECUTE AND ECHO
	/*///------------------------------------------------------------------------
	FINAL PUBLIC FUNCTION PDO_QUERY_ECHO()
	{
		$me = func_get_args();
		$types = 'string, closure, array, array, bool, bool';
		$names = 'query, action, parameters, skip, read_all, read';
		$default = [ SELF::$HANDLE_FILE, [], $this->VARIABLES['SKIP'], true, true ];
		if ( !SELF::ARGS( $me, $types, $default, $names ) ) return SELF::FAIL;
		
		// PREPARE ARGUMENTS AND EXECUTE QUERY
		$arguments = [ $me->query, $me->parameters, $me->skip, $me->read_all, $me->read ];
		return SELF::ARRAY_RECURSIVE_LOOP ( $this->PDO_QUERY (...$arguments)['result'], $me->action );
	}
	
	/*///------------------------------------------------------------------------
			>>> PDO EXECUTE QUERY AND GET RESULT
	/*///------------------------------------------------------------------------
	FINAL PUBLIC FUNCTION PDO_QUERY_RESULT(): ARRAY
	{
		return $this->PDO_QUERY( ...func_get_args() )['result'];
	}
	
	/*///------------------------------------------------------------------------
			>>> PDO QUERY EXECUTE
	/*///------------------------------------------------------------------------
	FINAL PUBLIC FUNCTION PDO_QUERY(): ARRAY
	{
		$me = func_get_args();
		$types = 'string, array, array, bool, bool';
		$names = 'query, parameters, skip, read_all, read';
		$default = [ [], $this->VARIABLES['SKIP'], true, true ];
		if ( !SELF::ARGS( $me, $types, $default, $names ) ) return SELF::FAIL;
		
		// OBTAIN REQUEST PARAMETERS IF EMPTY
		$me->parameters = $_REQUEST;
		
		// CREATE SQL CONNECTION IF IT DOESN'T EXIST
		if ( $this->VARIABLES['CONNECTION'] == NULL )
		{
			$this->VARIABLES['CONNECTION'] = $this->PDO_CONNECT();
		}
		
		// PREPARE PARAMETERS
		$PreparedParameters = $this->PDO_PARAMETERS ( $me->parameters, $me->skip );
		
		// CHECK FOR PARAMETERS THAT ARE NOT IN THE QUERY
		foreach ( array_keys ( $PreparedParameters ) as $key )
		{
			if ( strpos( $me->query, $key ) === false )
			{
				// REMOVE KEY IF NOT FOUND IN QUERY
				unset ( $PreparedParameters[$key] );
			}
		}
		
		// PREPARE THE SQL QUERY
		$PDOStatement = $this->VARIABLES['CONNECTION']->prepare( $me->query );
		
		// BIND THE PARAMETERS AND EXECUTE
		$success_status = $PDOStatement->execute( $PreparedParameters );
		
		// FIND OUT IF QUERY IS A RETRIEVE METHOD
		$query_arr = explode ( ' ', trim ( $me->query ) )[0];
		$is_select = ( stripos ( $query_arr, 'SELECT' ) !== false );
		
		if ( $me->read && $success_status && $is_select )
		{
			// READ ALL RESULTS
			$METHOD = 'PDO_COPY_RESULT';
			
			// CHECK IF SINGLE RESULT READ
			if ( !$me->read_all )
			{
				// SINGLE RESULT READ
				$METHOD = 'PDO_READ';
			}
			
			// READ THE DATA RESULT(S)
			$data = SELF::$METHOD ( $PDOStatement );
			
			// CLOSE THE QUERY
			$PDOStatement = NULL;
			
			// CHECK IF DATA RETURNS FALSE
			if ( is_bool ( $data ) && !$data )
			{
				$data = [];
			}
			
			// CHECK IF DATA IS AN ARRAY
			if (!is_array($data))
			{
				$data[] = $data;
			}
			
			// IF AUTODISCONNECT IS ON
			if ( SELF::$AUTODISCONNECT )
			{
				// DISCONNECT FROM SQL SERVER
				$this->PDO_DISCONNECT();
			}
			
			// RETURN THE DATA
			return [ 'status'=>$success_status, 'result'=>$data ];
		}
		else if ( $me->read && !$success_status && $is_select )
		{
			// CLOSE THE QUERY
			$PDOStatement = NULL;
			
			// IF AUTODISCONNECT IS ON
			if ( SELF::$AUTODISCONNECT )
			{
				// DISCONNECT FROM SQL SERVER
				$this->PDO_DISCONNECT();
			}
			
			// RETURN EMPTY ARRAY IF SELECT FAILED
			return [ 'status'=>$success_status, 'result'=>[] ];
		}
		
		// IF AUTODISCONNECT IS ON
		if ( SELF::$AUTODISCONNECT )
		{
			// DISCONNECT FROM SQL SERVER
			$this->PDO_DISCONNECT();
			
			// RETURN THE RESULT OF THE SQL QUERY
			return [ 'status'=>$success_status, 'result'=>[] ];
		}
		
		// RETURN THE RESULT OF THE SQL QUERY
		return [ 'status'=>$success_status, 'result'=>[], 'statement'=>$PDOStatement ];
	}
	
	/*///------------------------------------------------------------------------
			>>> COPY THE RESULT OF PREPARED STATEMENT TO VARIABLE
	/*///------------------------------------------------------------------------
	FINAL PUBLIC STATIC FUNCTION PDO_COPY_RESULT
	(
			PDOStatement &$PDO,	// RESULT OF PDO QUERY
			array &$result = []	// VARIABLE TO STORE RESULT INTO
	): ARRAY
	{
		// READ EACH LINE
		while ( $row = $PDO->fetch(PDO::FETCH_ASSOC) ) 
		{
			$result[] = $row;
		}
		
		// ALSO RETURN THE RESULT
		return $result;
	}
	
	/*///------------------------------------------------------------------------
			>>> READ THE RESULT OF PREPARED STATEMENT
	/*///------------------------------------------------------------------------
	FINAL PUBLIC STATIC FUNCTION PDO_READ
	(
			PDOStatement &$PDO // RESULT OF PDO QUERY
	)
	{
		return $PDO->fetch(PDO::FETCH_ASSOC);
	}
	
	/*///------------------------------------------------------------------------
			>>> RETURN A CONDITION STRING TO BE USED FOR PDO PREPARED STATEMENTS
	/*///------------------------------------------------------------------------
	FINAL PUBLIC FUNCTION PDO_CONDITION
	(
			array $parameter,	// ARRAY CONTAINING THE PARAMETERS
			$operator = 'AND',	// OPERATORS FOR CONDITIONAL STATEMENT
			array $skip = []	// ARRAY INDICES TO SKIP
	): STRING
	{
		// SET SKIP TO DEFAULT
		if ( empty ( $skip ) ) { $skip = $this->VARIABLES['SKIP']; }
		
		// PREPARE THE RETURN VALUE AND COUNTER FOR THE OPERATOR
		$return = '';
		$count = 0;
		
		// SPLIT OPERATOR INTO ARRAY
		$operator = SELF::DISASSEMBLE($operator);
		
		// READ PARAMETERS
		foreach($parameter as $array_key=>$value)
		{
			// CHECK IF PARAMETERS SHOULD BE SKIPPED
			if ( !in_array ( $array_key, $skip ) )
			{
				// CHECK IF THE CURRENT PARAMETER IS AN ARRAY
				if (!is_array($value))
				{
					// ATTACH OPERATORS TO THE CONDITIONAL STATEMENT
					$this->PDO_ATTACH_OPERATORS($count, $operator, $return);
					
					// ADD CONDITION TO RETURN STRING
					$return .=	" ( {$array_key} = :{$array_key} ".
								"AND {$array_key} LIKE :{$array_key} )";
				}
				else foreach(array_keys($value) as $inner_array_key)
				{
					// ATTACH OPERATORS TO THE CONDITIONAL STATEMENT
					$this->PDO_ATTACH_OPERATORS($count, $operator, $return);
					
					// ADD CONDITION TO RETURN STRING
					$return .=	" ( {$array_key} = :{$array_key}{$inner_array_key} ".
								"AND {$array_key} LIKE :{$array_key}{$inner_array_key} )";
				}
			}
		}
		
		// SEND BACK THE RESULTS
		return $return;
	}
	
	/*///------------------------------------------------------------------------
			>>> ATTACH OPERATORS TO CONDITIONAL STATEMENT
	/*///------------------------------------------------------------------------
	FINAL PRIVATE FUNCTION PDO_ATTACH_OPERATORS
	(
			int &$count,		// ARRAY INDEX
			array &$operator,	// OPERATOR ARRAY
			string &$return		// RETURN VARIABLE
	)
	{
		// CHECK IF THE RETURN STRING IS EMPTY
		if ($return != '')
		{
			// GET THE OPERATOR FROM THE OPERATOR ARRAY
			// IF OPERATOR RUNS OUT, USE THE DEFAULT OPERATOR
			$newOperator = strtoupper
			(
				SELF::ARRAY_KEY
				(
					$operator,
					$count,
					$this->VARIABLES['DEFAULT_OPERATOR']
				)
			);
			
			// ADD THE OPERATOR AND/OR TO RETURN STRING
			$return .= " {$newOperator}";
			
			// PROCEED TO THE NEXT OPERATOR INDEX
			$count++;
		}
	}
	
	/*///------------------------------------------------------------------------
			>>> SELECT AND ECHO FROM DATABASE
	/*///------------------------------------------------------------------------
	FINAL PUBLIC FUNCTION PDO_SELECT_AUTO_ECHO()
	{
		$me = func_get_args();
		$types = 'string, string, string, closure, array, bool, bool';
		$names = 'table, columns, operator, action, skip, read_all, read';
		$default = ['*', 'AND', NULL, $this->VARIABLES['SKIP'], true, true];
		if ( !SELF::ARGS( $me, $types, $default, $names ) ) return SELF::FAIL;
		
		// PREPARE THE ARGUMENTS
		$args = [ $me->table, $me->columns, $me->operator, $me->skip, $me->read_all, $me->read ];
		
		// EXECUTE
		$result = $this->PDO_SELECT_AUTO(...$args);
		
		// CHECK IF EMPTY RESULT
		if ( count ( $result ) == 0 ) return null;
		
		// DISPLAY RESULT IF SUCCESSFUL
		return SELF::ARRAY_RECURSIVE_LOOP ( $result['result'], $me->action );
	}
	
	/*///------------------------------------------------------------------------
			>>> PDO SELECT QUERY WITH CONDITION AUTOBUILD
				string $table,				// TABLE TO USE
				string $columns = '*',		// COLUMNS TO SELECT
				string $operator = 'AND',	// OPERATOR(S) TO USE
				array $skip = [],			// ARRAY INDICES TO SKIP
				bool $read_all = true,		// READ ALL RESULTS IN SELECT OPERATION?
				bool $read = true			// READ THE RESULT IN SELECT OPERATION?
	/*///------------------------------------------------------------------------
	FINAL PUBLIC FUNCTION PDO_SELECT_AUTO():ARRAY
	{
		$me = func_get_args();
		$types = 'string, string, string, array, bool, bool';
		$names = 'table, columns, operator, skip, read_all, read';
		$default = ['*', 'AND', $this->VARIABLES['SKIP'], true, true];
		if ( !SELF::ARGS( $me, $types, $default, $names ) ) return [];
		
		// OBTAIN REQUEST PARAMETERS IF EMPTY
		$parameters = $_REQUEST;
		
		// PREPARE A RETURN ARRAY
		$return = [];
		
		// IF CONNECTION IS DEAD, RETURN AN EMPTY ARRAY
		if ($this->VARIABLES['CONNECTION'] === NULL)
		{
			return $return;
		}
		
		// CREATE CONDITIONS
		$condition = $this->PDO_CONDITION ( $parameters, $me->operator, $me->skip );
		
		// BUILD THE QUERY STRING
		$query = SELF::QUERY_SELECT( $me->table, $condition, $me->columns);
		
		// EXECUTE THE QUERY STRING
		$return = $this->PDO_QUERY ( $query, $parameters, $me->skip, $me->read_all, $me->read );
		
		// RETURN THE RESULTS
		return $return;
	}
	
	/*///------------------------------------------------------------------------
			>>> RETURN A SELECT QUERY
	/*///------------------------------------------------------------------------
	FINAL PUBLIC STATIC FUNCTION QUERY_SELECT
	(
			string $table,			// TABLE TO USE
			string $condition = '',	// CONDITIONS IN QUERY STRING
			string $columns = '*'	// COLUMNS TO SELECT
	): STRING
	{
		// BUILD THE FIRST PART OF THE QUERY STRING
		$query = "SELECT {$columns} FROM {$table}";
		
		// APPEND THE CONDITIONS
		if ( $condition != '' )
		{
			$query .= " WHERE {$condition}";
		}
		
		// RETURN THE QUERY STRING
		return $query;
	}
	
}


/*///----------------------------------------------------------------------------
---------------------------------------------------------------------------------
	VIDEO AND AUDIO STREAMING
---------------------------------------------------------------------------------
/*///----------------------------------------------------------------------------
CLASS MEDIA
{
	private $path	= "";		// PATH TO MEDIA FILE
	private $stream	= "";		// FILE STREAM
	private $buffer	= 102400;	// BUFFER RATE
	private $start	= -1;		// STARTING BYTES
	private $end	= -1;		// ENDING BYTES
	private $size	= 0;		// MEDIA FILE SIZE
	
	/*///------------------------------------------------------------------------
			>>> CREATE VIDEO STREAM
	/*///------------------------------------------------------------------------
	FINAL PUBLIC FUNCTION __CONSTRUCT
	(
			$filePath	// PATH TO FILE
	) 
	{
		$this->path = $filePath;	// SET THE FILE PATH
		$this->START();				// BEGIN STREAMING
		die;						// END SCRIPT
	}
	
	/*///------------------------------------------------------------------------
			>>> START STREAMING VIDEO CONTENT
	/*///------------------------------------------------------------------------
	FINAL PUBLIC FUNCTION START()
	{
		$this->OPEN();		// OPEN THE VIDEO STREAM
		$this->SETHEADER();	// SET THE CONTENT HEADER
		$this->STREAM();	// STREAM THE DATA
		$this->end();		// END THE DATA STREAM
	}
	
	/*///------------------------------------------------------------------------
			>>> OPEN STREAM
	/*///------------------------------------------------------------------------
	FINAL PRIVATE FUNCTION OPEN()
	{
		if ( !( $this->stream = fopen ( $this->path, 'rb' ) ) )
		{
			die('Could not open stream for reading');
		}
	}
	
	/*///------------------------------------------------------------------------
			>>> SET PROPER HEADER TO SERVE THE VIDEO CONTENT
	/*///------------------------------------------------------------------------
	FINAL PRIVATE FUNCTION SETHEADER()
	{
		ob_get_clean();
		
		// GET FILE PROPERTIES
		$this->start = 0;												// GET STARTING BYTES
		$this->size  = filesize ( $this->path );						// GET FILE SIZE
		$this->end   = $this->size - 1;									// GET ENDING BYTES
		$mimetype = strtolower ( mime_content_type ( $this->path ) );	// GET CONTENT TYPE
		if
		(
			// IF THE CURRENT FILE IS AN OCTET-STREAM
				$mimetype = 'application/octet-stream' &&
				
			// AND THE FILE EXTENSION IS MP3
				strtolower ( pathinfo ( $this->path, PATHINFO_EXTENSION ) ) == 'mp3'
		)
		{
			// SET THE MIMETYPE TO AUDIO/MP3
			$mimetype = 'audio/mp3';
		}
		
		// SET HEADERS
		header ( "Content-Type: $mimetype" );
		header ( "Cache-Control: max-age=2592000, public" );
		header ( "Expires: " . gmdate ( 'D, d M Y H:i:s', time() + 2592000 ) . ' GMT' );
		header ( "Last-Modified: " . gmdate ( 'D, d M Y H:i:s', @filemtime ( $this->path ) ) . ' GMT' );
		header ( "Accept-Ranges: 0-" . $this->end );
		
		// IF BYTE RANGE IS SET
		if ( isset ( $_SERVER ['HTTP_RANGE'] ) )
		{
			$c_start = $this->start;
			$c_end = $this->end;
			
			// GET THE HTTP-RANGE
			list ( , $range ) = explode ( '=', $_SERVER['HTTP_RANGE'], 2 );
			
			if ( strpos ( $range, ',' ) !== false )
			{
				header ( 'HTTP/1.1 416 Requested Range Not Satisfiable' );
				header ( "Content-Range: bytes {$this->start}-{$this->end}/{$this->size}" );
				exit;
			}
			
			// IF RANGE IS EMPTY
			if ($range == '-')
			{
				// START AT THE END OF FILE
				$c_start = $this->size - substr ( $range, 1 );
			}
			else
			{
				// ELSE GET THE BEGINNING AND END RANGE
				$range = explode ( '-', $range );
				$c_start = $range [ 0 ];
				$c_end = ( isset ( $range [ 1 ] ) && is_numeric ( $range [ 1 ] ) ) ? $range [ 1 ] : $c_end;
			}
			
			// LIMIT THE END RANGE TO $this->end
			$c_end = ( $c_end > $this->end ) ? $this->end : $c_end;
			if ( $c_start > $c_end || $c_start > $this->size - 1 || $c_end >= $this->size )
			{
				header('HTTP/1.1 416 Requested Range Not Satisfiable');
				header("Content-Range: bytes {$this->start}-{$this->end}/{$this->size}");
				exit;
			}
			
			// SET THE BEGINNING AND END FROM TEMPORARY VARIABLES
			$this->start = $c_start;
			$this->end = $c_end;
			
			// COMPUTE THE LENGTH OF RANGE
			$length = $this->end - $this->start + 1;
			
			// SEEK THE DECLARED RANGE
			fseek ( $this->stream, $this->start );
			
			// SET THE HEADERS
			header ( 'HTTP/1.1 206 Partial Content' );
			header ( "Content-Length: $length" );
			header ( "Content-Range: bytes {$this->start}-{$this->end}/{$this->size}" );
		}
		else
		{
			// SET FILE SIZE AS DEFAULT LENGTH
			header("Content-Length: {$this->size}");
		}  
		 
	}
	
	/*///------------------------------------------------------------------------
			>>> PERFORM THE STREAMING OF CALCULATED RANGE
	/*///------------------------------------------------------------------------
	FINAL PRIVATE FUNCTION STREAM()
	{
		$i = $this->start;		// START
		set_time_limit ( 0 );	// LIMIT EXECUTION TIME OF SCRIPT
		
		// ITERATE THROUGH THE END OF THE STREAM
		while ( !feof ( $this->stream ) && $i <= $this->end )
		{
			// GET THE BUFFER SIZE
			$bytesToRead = $this->buffer;
			
			// CHECK IF INDEX + BUFFER EXCEEDS THE END OF FILE
			if ( ( $i + $bytesToRead ) > $this->end )
			{
				// COMPUTE THE LAST REMAINING BYTES
				$bytesToRead = $this->end - $i + 1;
			}
			
			// READ SPECIFIC NUMBER OF BYTES FROM STREAM
			$data = fread ( $this->stream, $bytesToRead );
			
			// ECHO AND FLUSH THE BUFFER OUTPUT
			echo $data;
			flush();
			
			// COMPUTE THE NUMBER OF BYTES READ
			$i += $bytesToRead;
		}
	}
	
	/*///------------------------------------------------------------------------
			>>> CLOSE CURRENTLY OPENED STREAM
	/*///------------------------------------------------------------------------
	FINAL PRIVATE FUNCTION END()
	{
		fclose ( $this->stream );	// CLOSE THE STREAM
		exit;						// END THE SCRIPT
	}
}


/*///----------------------------------------------------------------------------
---------------------------------------------------------------------------------
	INTERFACE SCRIPTS
---------------------------------------------------------------------------------
/*///----------------------------------------------------------------------------
CLASS GUI
{
	PUBLIC STATIC $VIDEO_MIME = [	// ACCEPTED VIDEO MIME TYPES
									'video/mp4',
									'video/webm',
									'video/ogg',
									'video/mkv'
								];
	PUBLIC STATIC $AUDIO_MIME = [	// ACCEPTED AUDIO MIME TYPES
									'audio/mp3',
									'audio/mpeg',
									'audio/mpeg3',
									'audio/x-mpeg',
									'audio/x-mpeg-3',
									'application/octet-stream',
									'audio/ogg',
									'audio/wav'
								];
	PUBLIC STATIC $AUDIO_EXTENSIONS =	[	// ACCEPTED AUDIO EXTENSIONS
											'mp3',
											'wav'
										];
	PUBLIC STATIC $IMAGE_EXTENSIONS =	[	// ACCEPTED IMAGE EXTENSIONS
											'jpg',
											'jpeg',
											'bmp',
											'png',
											'gif'
										];
	
	
	/*///------------------------------------------------------------------------
	-----------------------------------------------------------------------------
*		METHODS APPLICABLE ONLY TO THIS CLASS
	-----------------------------------------------------------------------------
	/*///------------------------------------------------------------------------
	
	
	/*///------------------------------------------------------------------------
			>>> DECIDE IF RESULT SHOULD BE ECHOED (AUTO-ECHO IF CALLED BY FUNCTION)
	/*///------------------------------------------------------------------------
	FINAL PRIVATE STATIC FUNCTION ECHO_RESULT
	(
			bool $echo_result = null,				// FILE BASENAME
			string $caller_function = 'EXECUTE',	// FUNCTION NAME
			string $caller_class = 'TASK'			// CLASS NAME
	): BOOL
	{
		// GET THE NAME OF THE CALLER FUNCTION
		$caller = TASK::CALLER(3);
		
		return	(
					// ECHO A RESULT IF...
					// THE USER DID NOT SPECIFY WHETHER TO ECHO A RESULT OR NOT AND..
					$echo_result == null &&
					
					// THE CALLER FUNCTION IS $caller_class::$caller_function
					strtoupper ( $caller['function'] ) == $caller_function &&
					strtoupper ( $caller['class'] ) == $caller_class
					
					// OTHERWISE DO NOT ECHO A RESULT IF THE USER DID NOT SPECIFY TO DO SO
				) ? true : ( $echo_result == null ? false : $echo_result );
	}
	
	/*///------------------------------------------------------------------------
			>>> RETURN THE FILENAME WITHOUT THE FILE EXTENSION IF NOT BLANK
	/*///------------------------------------------------------------------------
	FINAL PRIVATE STATIC FUNCTION UNIQUE_NAME
	(
			string $filename = ''	// FILE BASENAME
	): STRING
	{
		// USE THE FULL FILENAME IF THE FILENAME WITHOUT
		// THE FILE EXTENSION IS BLANK (i.e. ".htaccess")
		return	( explode ( '.', $filename )[0] !== '' ) ?
				substr ( $filename, 0, strrpos ( $filename, '.' ) ) : $filename;
	}
	
	/*///------------------------------------------------------------------------
			>>> FORMAT THE VARIABLE NAMES OF GET URLs
	/*///------------------------------------------------------------------------
	FINAL PRIVATE STATIC FUNCTION VAR_OF_GET
	(
			string $varname = ''	// VARIABLE NAME TO BE USED IN GET URL
	): STRING
	{
		//  APPLY FORMATTING IF THE VARIABLE NAME IS NOT BLANK
		if ( $varname !== '' )
		{
			// MAKE SURE THAT THE VARIABLE NAME BEGINS IN '?'
			if ( strpos ( $varname, '?' ) !== 1 )
			{
				$varname = "?$varname";
			}
			
			// MAKE SURE THAT THE VARIABLE NAME ENDS IN '='
			if ( strpos ( $varname, '=' ) !== strlen ( $varname ) )
			{
				$varname .= '=';
			}
		}
		
		return $varname;
	}
	
	
	/*///------------------------------------------------------------------------
	-----------------------------------------------------------------------------
*		FILE VALIDATION
	-----------------------------------------------------------------------------
	/*///------------------------------------------------------------------------
	
	
	/*///------------------------------------------------------------------------
			>>> CHECK IF FILE IS VIDEO
	/*///------------------------------------------------------------------------
	FINAL PUBLIC STATIC FUNCTION IS_VIDEO
	(
			string $filename,		// FILE NAME/LOCATION
			string $mimetype = '',	// FILE MIMETYPE
			string $extension = ''	// FILE EXTENSION
	): BOOL
	{
		// GET THE FILENAME/MIMETYPE/EXTENSION
		TASK::LOCATION_INFO ( $filename, $mimetype, $extension );
		
		// RETURN TRUE IF THE REFERENCED FILE IS A VIDEO FILE
		return ( is_file ( $filename ) && in_array ( $mimetype, SELF::$VIDEO_MIME ) );
	}
	
	/*///------------------------------------------------------------------------
			>>> CHECK IF FILE IS AUDIO
	/*///------------------------------------------------------------------------
	FINAL PUBLIC STATIC FUNCTION IS_AUDIO
	(
			string $filename,		// FILE NAME/LOCATION
			string $mimetype = '',	// FILE MIMETYPE
			string $extension = ''	// FILE EXTENSION
	): BOOL
	{
		// GET THE FILENAME/MIMETYPE/EXTENSION
		TASK::LOCATION_INFO ( $filename, $mimetype, $extension );
		
		// RETURN TRUE IF THE REFERENCED FILE IS AN AUDIO FILE
		return (
					is_file ( $filename ) &&
					in_array ( $mimetype, SELF::$AUDIO_MIME ) &&
					in_array ( $extension, SELF::$AUDIO_EXTENSIONS )
				);
	}
	
	/*///------------------------------------------------------------------------
			>>> CHECK IF FILE IS AN IMAGE
	/*///------------------------------------------------------------------------
	FINAL PUBLIC STATIC FUNCTION IS_IMAGE
	(
			string $filename,		// FILE NAME/LOCATION
			string $mimetype = '',	// FILE MIMETYPE
			string $extension = ''	// FILE EXTENSION
	): BOOL
	{
		// GET THE FILENAME/MIMETYPE/EXTENSION
		TASK::LOCATION_INFO ( $filename, $mimetype, $extension );
		
		// RETURN TRUE IF THE REFERENCED FILE IS AN IMAGE FILE
		return ( is_file ( $filename ) && in_array ( $extension, SELF::$IMAGE_EXTENSIONS ) );
	}
	
	
	/*///------------------------------------------------------------------------
	-----------------------------------------------------------------------------
*		GUI BUILDER
	-----------------------------------------------------------------------------
	/*///------------------------------------------------------------------------
	
	
	/*///------------------------------------------------------------------------
			>>> CREATE INTERNAL STYLE
	/*///------------------------------------------------------------------------
	FINAL PUBLIC STATIC FUNCTION STYLE
	(
			string $lock = 'auto',		// ADJUST WIDTH/HEIGHT/AUTO
			int $thumbsize = 150,		// SIZE OF THUMBNAIL IN PX
			int $tabs = 2,				// NUMBER OF TABS / INDENTATION
			bool $echo_result = null	// ECHO STRING RESULT?
	): STRING
	{
		// SET DEFAULT VALUE FOR ECHO RESULT IF NOT SET
		$echo_result = SELF::ECHO_RESULT( $echo_result );
		
		// USE THE IMAGE WIDTH AS THE DEFAULT THUMBNAIL CSS SIZE LIMIT
		$thumb = ( $lock != 'width' && $lock != 'height' ) ? 'width' : $lock;
		
		// (COMPUTE THE CONTAINER SIZE) MAKE THE CONTAINER SIZE 20 PIXELS LARGER THAN THE TUMBNAIL
		$containersize = $thumbsize + 20;
		
		// SET THE SIZE OF THE DIV CONTAINING THE FILENAME
		$namesize = ( $lock != 'width' && $lock != 'auto' ) ? "100%" : "{$containersize}px";
		
		// COMPUTE THE FONTSIZE BASED ON THE SIZE OF THE THUMBNAIL
		$fontsize = $thumbsize / 10;
		
		// FINALIZE THE CSS SCRIPT
		$result = str_replace ( "\r\n", "\r\n" . TASK::STRING_OF ( "\t", $tabs ),
<<<SCRIPT

<script>image_wait( 'img', 600 );</script>
<style>
img.icon, img.thumbnail, div.icon, div.thumbnail {
	{$thumb}:	{$thumbsize}px;
}

div.container {
	width:		{$containersize}px;
}

div.container
div.filename {
	font-size:	{$fontsize}px;
	width:		{$namesize}px;
}
</style>

SCRIPT
		);
		
		// ECHO THE RESULT OR...
		if ( $echo_result )
		{
			echo $result;
			die;
		}
		
		// RETURN THE CSS STRING FOR FURTHER PROCESSING
		return $result;
	}
	
	/*///------------------------------------------------------------------------
			>>> LIST THE CONNECTED DISK DRIVES
	/*///------------------------------------------------------------------------
	FINAL PUBLIC STATIC FUNCTION DISKS
	(
			string $url_varname,			// VARIABLE NAME OF URL
			string $url_extras = '',		// ADDITIONAL PARAMETERS FOR ANCHOR
			int $tabs = 2,					// NUMBER OF TABS / INDENTATION
			string $target = 'div.ajax',	// TARGET DIV FOR AJAX LINKS
			bool $includeHome = true,		// INCLUDE HOME FOLDER?
			string $homeDir = ''			// PREFERRED HOME DIRECTORY
	): STRING
	{
		// CREATE THE INDENTATION
		$stringtabs = "\r\n" . TASK::STRING_OF ( "\t", $tabs );
		
		// GET CURRENT WORKING DIRECTORY AND ITS AVAILABLE SPACE
		$homeDir = ( $homeDir != '' ) ? $homeDir : getcwd();
		$workDir = rawurlencode ( TASK::STANDARD_PATH ( $homeDir ) );
		$wdspace = TASK::BYTES ( disk_free_space ( $homeDir ) );
		
		// PREPARE HTML SCRIPT
		$diskDrives = !$includeHome ? '' : str_replace
		( "\r\n", $stringtabs, <<<DRIVE

<div class = 'container' >
	<a class='ajax' href='?{$url_varname}={$workDir}{$url_extras}' target='{$target}' >
		<div class='icon'>
