		<div class = 'filename'>
			[{$drive['letter']}]<br>{$drive['name']}<br>[{$space}]
		</div>
	</a>
</div>

DRIVE
			);
		}
		
		// CREATE THE SCRIPT FOR LOADING THE DISK DRIVES' THUMBNAIL ICON
		return $diskDrives . str_replace
			( "\r\n", $stringtabs, <<<DRIVE

<script>$('img#drive.icon').attr('src', drive);</script>

DRIVE
			);
	}
	
	/*///------------------------------------------------------------------------
			>>> CREATE VIDEO ELEMENT
	/*///------------------------------------------------------------------------
	FINAL PUBLIC STATIC FUNCTION VIDEO
	(
			string $file_url,			// VIDEO URL
			string $stream = '',		// VARIABLE NAME FOR VIDEO STREAM ?$stream=path/to/file.mp4
			string $download = '',		// VARIABLE NAME FOR VIDEO DOWNLOAD ?$download=path/to/file.mp4
			string $identifier = '',	// UNIQUE IDENTIFIER FOR RELATED VIDEO OBJECTS
			string $mimetype = '',		// VIDEO MIME TYPE
			string $rawname = '',		// RAW FILE NAME WITHOUT EXTENSION
			int $tabs = 2,				// NUMBER OF TABS / INDENTATION
			bool $echo_result = null,	// ECHO STRING RESULT?
			bool $defaultCtrl = false	// USE DEFAULT HTML5 VIDEO CONTROLS
	): STRING
	{
		// SET THE CONTROL ATTRIBUTE FOR HTML5 MEDIA
		$controls = $defaultCtrl ? 'controls' : '';
		
		// ATTACH THE CONTROLLER SCRIPT TO THE SELECTED HTML5 MEDIA ELEMENT
		$script = $defaultCtrl ? '' : "<script>active_video_controls($identifier);</script>";
		
		// SET DEFAULT VALUE FOR ECHO RESULT IF NOT SET
		$echo_result = SELF::ECHO_RESULT( $echo_result );
		
		// GET THE RAW FILE URL STRING
		$filename = rawurldecode ( $file_url );
		
		// ENSURE THAT THE FILE URL IS URLENCODED
		$file_url = rawurlencode ( $filename );
		
		// CHECK IF THE FILE URL REFERENCES TO AN ACTUAL FILE
		if ( !is_file ( $filename ) ) return '';
		
		// CHECK IF THE FILENAME IS READABLE / NOT A PROTECTED FILE
		if ( !is_readable ( $filename ) ) return '';
		
		// CHECK IF THE FILENAME IS A DIRECTORY
		if ( is_dir ( $filename ) ) return '';
		
		// CHECK IF THE FILENAME IS '..' (GO UP ONE FOLDER)
		if ( ( $name = basename ( $filename ) ) == '..' ) return '';
		
		// APPLY FORMATTING IF THE VIDEO STREAM URL'S VARIABLE NAME IS NOT EMPTY
		$stream = SELF::VAR_OF_GET( $stream );
		
		// APPLY FORMATTING IF THE DIRECT DOWNLOAD URL'S VARIABLE NAME IS NOT EMPTY
		$download = SELF::VAR_OF_GET( $download );
		
		// READ THE MIME TYPE IF IT IS NOT MANUALLY DECLARED BY USER
		$mimetype = ( $mimetype != '' ) ? $mimetype : strtolower ( mime_content_type ( $filename ) );
		
		// CHECK IF THE MIME TYPE IS ACCEPTED
		if ( !in_array ( $mimetype, SELF::$VIDEO_MIME ) ) return '';
		
		// IF THE RAW FILENAME / DISPLAY NAME IS NOT DECLARED
		// USE THE FULL FILENAME AS THE DISPLAY NAME IF THE FILENAME
		// WITHOUT COUNTING THE FILE EXTENSION IS BLANK (i.e. ".htaccess")
		$rawname = ( $rawname == '' ) ? SELF::UNIQUE_NAME ( $name ) : $rawname;
		
		// BUILD THE HTML SCRIPT FOR THE HTML5 MEDIA ELEMENT
		$result = str_replace ( "\r\n", "\r\n" . TASK::STRING_OF ( "\t", $tabs ),
<<<SCRIPT

<!-- $filename -->
<div class = 'media-container' >
	<div id = "video-container$identifier" class = "video-container" >
		<div id = "video-name$identifier" class = "video-name" title="$rawname" >
			<a href='$download$file_url' target='_blank' >
				<font size='4' style='vertical-align:middle;'>&#8681;</font>
				$rawname
			</a>
		</div>
		<div class = "video-screen-area-container" >
			<!-- Video -->
			<div id = 'video-screen-mode-container$identifier' class = "video-screen-area" >
				<video id="video$identifier" $controls>
					<source src='$stream$file_url' type='$mimetype'>
					Your browser doesn't support this video.
					<a href='$download$file_url' target='_blank' >
						Click here to download instead.
					</a>
				</video>
				<!-- Video Controls -->
				<div id = "video-control-container$identifier" class = "video-control-toggle-area" >
					<div id = "video-controls$identifier" class = "video-control-container" >
						<div class = "play-button-container" >
							<button id = "play-pause$identifier" type = "button" >&#9654;</button>
						</div>
						<div class = "seek-bar-container" >
							<input
									id = "seek-bar$identifier"
									type = "range"
									min = "0"
									max = "1"
									step = "0.01"
									value = "0"
							>
						</div>
						<div class = "mute-button-container" >
							<button id = "mute$identifier" type = "button" >&#128264;</button>
						</div>
						<div class = "volume-bar-container" >
							<input
									id = "volume-bar$identifier"
									type = "range"
									min = "0"
									max = "1"
									step = "0.01"
									value = "1"
							>
						</div>
						<div class = "full-screen-button-container" >
							<button id = "full-screen$identifier" type = "button" >&#9974;</button>
						</div>
					</div>
				</div>
			</div>
		</div>
	</div>
</div>$script

SCRIPT
		);
		
		if ( $echo_result )
		{
			echo $result;
			die;
		}
		
		return $result;
	}
	
	/*///------------------------------------------------------------------------
			>>> CREATE AUDIO ELEMENT
	/*///------------------------------------------------------------------------
	FINAL PUBLIC STATIC FUNCTION AUDIO
	(
			string $file_url,			// AUDIO URL
			string $stream = '',		// VARIABLE NAME FOR AUDIO STREAM ?$stream=path/to/file.mp4
			string $download = '',		// VARIABLE NAME FOR AUDIO DOWNLOAD ?$download=path/to/file.mp4
			string $mimetype = '',		// AUDIO MIME TYPE
			string $rawname = '',		// RAW FILE NAME WITHOUT EXTENSION
			int $tabs = 2,				// NUMBER OF TABS / INDENTATION
			bool $echo_result = null,	// ECHO STRING RESULT?
			bool $defaultCtrl = true	// USE DEFAULT HTML5 AUDIO CONTROLS
	): STRING
	{
		// SET THE CONTROL ATTRIBUTE FOR HTML5 MEDIA
		$controls = $defaultCtrl ? 'controls' : '';
		
		// SET DEFAULT VALUE FOR ECHO RESULT IF NOT SET
		$echo_result = SELF::ECHO_RESULT( $echo_result );
		
		// LIST THE ACCEPTED MIME TYPES
		$accepted = [
						'audio/mp3',
						'audio/mpeg',
						'audio/mpeg3',
						'audio/x-mpeg',
						'audio/x-mpeg-3',
						'application/octet-stream',
						'audio/ogg',
						'audio/wav'
					];
		
		// GET THE RAW FILE URL STRING
		$filename = rawurldecode ( $file_url );
		
		// ENSURE THAT THE FILE URL IS URLENCODED
		$file_url = rawurlencode ( $filename );
		
		// GET THE FILE EXTENSION
		$extension = strtolower ( pathinfo ( $filename, PATHINFO_EXTENSION ) );
		
		if
		(
			// IF THE FILE EXTENSION IS BLANK OR..
				$extension == '' ||
				
			// NOT A FILE OR..
				!is_file ( $filename ) ||
				
			// NOT A READABLE FILE OR..
				!is_readable ( $filename ) ||
				
			// FILENAME IS A REFERENCE TO A DIRECTORY OR..
				is_dir ( $filename ) ||
				
			// A REFERENCE TO THE PARENT DIRECTORY
				( $name = basename ( $filename ) ) == '..'
		) return ''; // STOP METHOD EXECUTION
		
		// APPLY FORMATTING IF THE AUDIO STREAM URL'S VARIABLE NAME IS NOT EMPTY
		$stream = SELF::VAR_OF_GET( $stream );
		
		// APPLY FORMATTING IF THE DIRECT DOWNLOAD URL'S VARIABLE NAME IS NOT EMPTY
		$download = SELF::VAR_OF_GET( $download );
		
		// READ THE MIME TYPE IF IT IS NOT MANUALLY DECLARED BY USER
		$mimetype = ( $mimetype != '' ) ? $mimetype :
					(	(	$mimetype = strtolower ( mime_content_type ( $filename ) )
						)	== 'application/octet-stream' && $extension == 'mp3'
					) ? 'audio/mp3' : $mimetype;
		
		// CHECK IF THE MIME TYPE IS ACCEPTED
		if ( !in_array ( $mimetype, $accepted ) ) return '';
		
		// IF THE RAW FILENAME / DISPLAY NAME IS NOT DECLARED
		// USE THE FULL FILENAME AS THE DISPLAY NAME IF THE FILENAME
		// WITHOUT COUNTING THE FILE EXTENSION IS BLANK (i.e. ".htaccess")
		$rawname = ( $rawname == '' ) ? SELF::UNIQUE_NAME ( $name ) : $rawname;
		
		// APPLY ADDITIONAL FORMATTING TO RAW FILENAME
		$rawname = str_replace ( '+', ' ', str_replace ( '_', ' ', $rawname ) );
		
		// BUILD THE HTML SCRIPT FOR THE HTML5 MEDIA ELEMENT
		$result = str_replace ( "\r\n", "\r\n" . TASK::STRING_OF ( "\t", $tabs ),
<<<SCRIPT

<div class = 'media-container' >
	<div id="audio-container" class = "audio-container" >
		<div id="Audio-name" class = "audio-name-container" title="$rawname">
			<a href='$download$file_url' target='_blank' >
				<font size='4' style='vertical-align:middle;'>&#8681;</font>
				$rawname
			</a>
		</div>
		<audio $controls>
			<source src='$stream$file_url' type='$mimetype'>
			HTML5 Audio Not Supported
		</audio>
	</div>
</div>

SCRIPT
		);
		
		if ( $echo_result )
		{
			echo $result;
			die;
		}
		
		return $result;
	}
	
	/*///------------------------------------------------------------------------
			>>> CREATE GENERIC ICONS FOR VIDEO, AUDO, AND FOLDER
	/*///------------------------------------------------------------------------
	FINAL PUBLIC STATIC FUNCTION ICON
	(
			string $file_url,				// FILE/FOLDER URL
			string $file_varname = '',		// FILE/FOLDER VARIABLE NAME
			int $thumbsize = 150,			// SIZE OF THUMBNAIL IN PX
			string $url_extras = '',		// EXTRA PARAMETERS FOR ANCHOR
			string $rawname = '',			// RAW FILE NAME WITHOUT EXTENSION
			int $fontsize = 0,				// FONTSIZE IN PX
			int $tabs = 2,					// NUMBER OF TABS / INDENTATION
			string $anchor_properties = '',	// ADDITIONAL PROPERTIES FOR ANCHOR
			bool $echo_result = null		// ECHO STRING RESULT?
	): STRING
	{
		// SET DEFAULT VALUE FOR ECHO RESULT IF NOT SET
		$echo_result = SELF::ECHO_RESULT( $echo_result );
		
		// COMPUTE THE FONT SIZE
		$fontsize = ( $fontsize == 0 ) ? ( $thumbsize / 10 ) : $fontsize;
		
		// GET THE RAW FILE URL STRING
		$filename = rawurldecode ( $file_url );
		
		// ENSURE THAT THE FILE URL IS URLENCODED
		$file_url = rawurlencode ( TASK::STANDARD_PATH ( realpath ( $filename ) ) );
		
		// READ THE MIME TYPE
		$mimetype = is_file ( $filename ) ? strtolower ( mime_content_type ( $filename ) ) : '';
		
		// GET THE FILE EXTENSION
		$extension = strtolower ( pathinfo ( $filename, PATHINFO_EXTENSION ) );
		
		// SET THE EXTENSIONS CONSIDERED AS AUDIO
		$audio_ext = ['mp3', 'wav'];
		
		// SET THE MIME TYPES CONSIDERED AS AUDIO
		$audios = [
						'audio/mp3',
						'audio/mpeg',
						'audio/mpeg3',
						'audio/x-mpeg',
						'audio/x-mpeg-3',
						'application/octet-stream',
						'audio/ogg',
						'audio/wav'
					];
		
		// SET THE MIME TYPES CONSIDERED AS VIDEO
		$videos = ['video/mp4', 'video/webm', 'video/ogg', 'video/mkv'];
		
		// IF FILE IS NOT READABLE, EXIT SCRIPT
		if ( !is_readable ( $filename ) ) return '';
		
		// CHECK IF THE FILENAME IS A REFERENCE TO A FOLDER
		$type = ( is_dir ( $filename ) ) ? 'folder' : 'any';
		
		// CHECK IF THE FILENAME IS A REFERENCE TO AN AUDIO FILE
		$type = ( in_array ( $mimetype, $audios ) && in_array ( $extension, $audio_ext )
				) ? 'audio' : $type;
		
		// CHECK IF THE FILENAME IS A REFERENCE TO A VIDEO FILE
		$type = ( in_array ( $mimetype, $videos ) ) ? 'video' : $type;
		
		// CREATE A UNIQUE IDENTIFIER FOR THE FILE'S HTML ELEMENTS
		$unique = md5 ( time() . rand ( 0,9999 ) );
		
		// GET THE PARENT DIRECTORY
		$requestDir = dirname ( TASK::REQUEST ( $file_varname ) ) . '/';
		
		// APPLY FORMATTING IF THE FILE / FOLDER VARIABLE NAME IS NOT EMPTY
		$file_varname = SELF::VAR_OF_GET( $file_varname );
		
		// EXTRACT RAW FILENAME IF NOT MANUALLY SET BY USER
		if ( $rawname == '' )
		{
			$rawname =	basename ( $filename );
			
			// WHEN A ZIP FILE IS OPENED, THE NAME OF THE WORKING DIRECTORY
			// IS LISTED AMONG THE FILES. THE REQUEST/WORKING DIRECTORY WILL
			// THUS BE THE SAME AS THE FILENAME.
			if ( $requestDir == $filename || $rawname == '..' )
			{
				$rawname = '../';
			}
			else if
			(
				// IF THE FILENAME IS NOT A REFERENCE TO A DIRECTORY
				// AND THE FILENAME IS NOT EMPTY (i.e. '.htaccess')
				// USE THE FILENAME WITHOUT ITS EXTENSION.
					!is_dir ( $filename ) &&
					explode ( '.', $rawname )[0] !== '' &&
					( $n = substr ( $rawname, 0, strrpos ( $rawname, '.' ) ) ) !== ''
			)
			{
				$rawname = $n;
			}
		}
		
		// DEDUCT 20PX PADDING OF OUTER DIV TO PX LIMIT
		// SPLIT THE RAW FILENAME TO MAKE IT FIT THE THUMBNAIL
		$rawname = TASK::PX_PER_WORD ( $rawname, $thumbsize - 20, $fontsize );
		
		// PREPARE THE PROPERTIES FOR THE HYPERLINK
		$anchor_properties = ( $anchor_properties != '' ) ? $anchor_properties : (
							 ( $type == 'any' ) ? "target='_blank'" : "class='ajax'" );
		
		// BUILD THE HTML SCRIPT FOR THE HTML5 MEDIA ELEMENT
		$result = str_replace ( "\r\n", "\r\n" . TASK::STRING_OF ( "\t", $tabs ),
<<<SCRIPT

<div class = 'container' >
	<a $anchor_properties href='$file_varname$file_url$url_extras' >
		<div class='icon'>
			<img id='$type$unique' class='icon' />
		</div>
		<div class='filename' >$rawname</div>
	</a>
</div>

SCRIPT
		);
		
		// INSERT THE JQUERY SCRIPT TO DISPLAY THE ICON IMAGE
		$result .= ( $result == '' ) ?
		'' : str_replace ( "\r\n", "\r\n" . TASK::STRING_OF ( "\t", $tabs ),
<<<SCRIPT

<script>$('img#$type$unique.icon').attr('src', $type);</script>

SCRIPT
		);
		
		if ( $echo_result )
		{
			echo $result;
			die;
		}
		
		return $result;
	}
	
