	/*///------------------------------------------------------------------------
			>>> CREATE ZIP THUMBNAIL
	/*///------------------------------------------------------------------------
	FINAL PUBLIC STATIC FUNCTION ZIP
	(
			string $file_url,							// FILE URL
			string $start_key = '',						// REQUEST KEY FOR START OF LIST
			string $end_key = '',						// REQUEST KEY FOR END OF LIST
			string $parent_varname = '',				// VARIABLE NAME FOR PARENT FOLDER
			string $parent_url_extras = '',				// EXTRA PARAMETERS FOR FOLDER ANCHOR
			string $zip_varname = '',					// VARIABLE NAME FOR ZIP FILE
			string $content_varname = '',				// VARIABLE NAME FOR ZIP CONTENT
			string $anchor_href_extras = '',			// EXTRA PARAMETERS FOR FILE ANCHOR
			string $anchor_props = "target='_blank'",	// ADDITIONAL PROPERTIES FOR ANCHOR
			string $zip_varname_th = '',				// VARIABLE NAME FOR ZIP FILE ( THUMBNAIL MODE )
			string $content_varname_th = '',			// VARIABLE NAME FOR ZIP CONTENT ( THUMBNAIL MODE )
			int $thumbsize = 150,						// SIZE OF THUMBNAIL IN PX
			int $fontsize = 0,							// FONTSIZE IN PX
			int $tabs = 2,								// NUMBER OF TABS / INDENTATION
			bool $display_all = null,					// DISPLAY ALL ZIP CONTENT?
			bool $echo_result = null					// ECHO STRING RESULT?
	): STRING
	{
		// SET DEFAULT VALUE FOR ECHO RESULT IF NOT SET
		$echo_result = SELF::ECHO_RESULT( $echo_result );
		
		// COMPUTE FONT SIZE
		$fontsize = ( $fontsize == 0 ) ? ( $thumbsize / 10 ) : $fontsize;
		
		// GET THE RAW FILE URL STRING
		$fn = rawurldecode ( $file_url );
		
		// ENSURE THAT THE FILE URL IS URLENCODED
		$file_url = rawurlencode ( $fn );
		
		// CHECK IF PARENT FOLDER IS THE SAME AS CURRENT FILE
		// THE PARENT FOLDER IS THE SAME AS CURRENT FILE IF A ZIP ARCHIVE IS OPEN
		$zipOpened = ( TASK::REQUEST ( $parent_varname ) === $fn );
		
		// HTML CODE:
		$overlay_ht = ''; // OVERLAY HTML CODE (I.E. FOR DISPLAYING IMAGES)
		$overlay_js = ''; // OVERLAY JAVASCRIPT FOR CONTROLLING OVERLAY AJAX CONTENT
		
		// AUTOMATICALLY DECIDE WHETHER TO DISPLAY ALL CONTENT OR NOT BASED ON
		// WHETHER A ZIP CONTENT IS OPEN OR NOT
		$display_all =	( $display_all !== null ) ? $display_all : $zipOpened;
		
		// IF THE FILENAME IS A REFERENCE TO THE PREVIOUS FOLDER ('..'),
		// THEN ADD A TRAILING '/' AT THE END OF A STRING
		$name = ( ( $f = basename ( $fn ) ) != '..' ) ? $f : $f . '/';
		
		// DEDUCT 20PX PADDING OF OUTER DIV TO PX LIMIT
		$name = TASK::PX_PER_WORD ( $name, $thumbsize - 20, $fontsize );
		
		// INSTANTIATE A TASK
		$task = new TASK;
		
		// FILE COUNTER FOR ENSURING UNIQUE HTML ELEMENT IDs
		$ctr = 0;
		
		// INITIALIZE RESULT
		$result = '';
		
		// INITIALIZE REQUEST KEYS $_REQUEST[$key]
		$request_keys = '';
		
		// USE REQUEST KEYS WHEN DISPLAYING ALL ZIP CONTENT
		if ( $display_all ) $request_keys = "$start_key, $end_key";
		
		// READ THE CONTENT(S) OF ZIP FILE
		$zipFolder = $task ( 'ZIPLIST', $request_keys, [$fn, true] );
		
		// GET $zipFolder['count'].. SET THE DEFAULT VALUE TO 0 IF NOT FOUND
		$fileCount = TASK::ARRAY_KEY ( $zipFolder, 'count', 0 );
		
		// CONVERT COMMA SEPARATED LIST TO ARRAY
		$zip_varname = TASK::DISASSEMBLE ( $zip_varname );
		$content_varname = TASK::DISASSEMBLE ( $content_varname );
		
		// ITERATE THROUGH ZIP CONTENTS
		foreach ( TASK::ARRAY_KEY ( $zipFolder, 'file', $zipFolder ) as $file)
		{
			// ENCODE FILENAME FROM ZIP CONTENT
			$f = rawurlencode ( $file );
			
			// CREATE A UNIQUE HASH FOR THE HTML ELEMENT
			$unique = md5 ( time() . rand ( 0,9999 ) );
			
			// IF A ZIP FILE IS OPENED
			// INSERT HTML IMAGE OVERLAY FOR ZIP ICONS
			if ( !$zipOpened )
			{
				// CREATE AN OVERLAY ELEMENT
				$overlay_ht =	"\r\n\t\t\t<div id='zip$ctr$unique' class='zip-overlay'>".
								"<img id='zip$ctr$unique' class='zip-overlay' />".
								"</div>";
								
				// PREPARE THE JAVASCRIPT FOR CONTROLLING THE OVERLAY FADE IN/OUT
				$overlay_js =	"\r\n<script>$('img#zip$ctr$unique.zip-overlay').attr('src', zip);</script>".
								"\r\n<script>zip_overlay_control('#zip$ctr$unique.zip-overlay');</script>";
			}
			
			// GET THE FILE EXTENSION
			$zfextension = strtolower ( pathinfo ( $file, PATHINFO_EXTENSION ) );
			
			// PREPARE THE ANCHOR PROPERTIES
			$anchor_props = ( $anchor_props === '' ) ? "target='_blank'" : $anchor_props;
			
			// THE FIRST ELEMENT IN $zip_varname IS
			// THE VARIABLE NAME OF THE ZIP ARCHIVE
			// WHEN THE FILE IS NOT AN IMAGE
			$zipvar = $zip_varname[0];
			
			// THE FIRST ELEMENT IN $content_varname IS
			// THE VARIABLE NAME OF THE ZIP CONTENT
			// WHEN THE FILE IS NOT AN IMAGE
			$convar = $content_varname[0];
			
			// BUILD THE HTML ELEMENT PROPERTY
			$property = "href='?$zipvar=$file_url&$convar=$f' target='_blank'";
			
			// IF THE EXTENSION SHOWS AN IMAGE FILE
			if ( in_array ( $zfextension, ['jpg', 'jpeg', 'bmp', 'png', 'gif'] ) )
			{
				// IF ALL CONTENTS ARE TO BE DISPLAYED
				if ( !$display_all )
				{
					// USE THIS PROPERTY
					$property = "href='?$parent_varname=$file_url$parent_url_extras' class='ajax'";
				}
				else
				{
					// THE SECOND ELEMENT IN $zip_varname IS
					// THE VARIABLE NAME OF THE ZIP ARCHIVE
					// WHEN THE FILE IS AN IMAGE
					if ( count ( $zip_varname ) > 1 ) $zipvar = $zip_varname[1];
					
					// THE SECOND ELEMENT IN $content_varname IS
					// THE VARIABLE NAME OF THE ZIP CONTENT
					// WHEN THE FILE IS AN IMAGE
					if ( count ( $content_varname ) > 1 ) $convar = $content_varname[1];
					
					// BUILD THE HTML ELEMENT PROPERTY
					$property = "href='?$zipvar=$file_url&$convar=$f$anchor_href_extras' $anchor_props";
					
					// REMOVE THE FILE EXTENSION
					$name = str_ireplace ( '.' . $zfextension, '', $file );
					
					// DEDUCT 20PX PADDING OF OUTER DIV TO PX LIMIT
					// SPLIT THE RAW FILENAME TO MAKE IT FIT THE THUMBNAIL
					$name = TASK::PX_PER_WORD ( $name, $thumbsize - 20, $fontsize );
				}
				
				// BUILD THE HTML SCRIPT FOR THE HTML ELEMENT
				$result .= str_replace ( "\r\n", "\r\n" . TASK::STRING_OF ( "\t", $tabs ),
<<<SCRIPT

<div class = 'container' >
	<a $property >
		<div class='thumbnail'>
			<img src='?$zip_varname_th=$file_url&$content_varname_th=$f' class='thumbnail' />$overlay_ht
		</div>
		<div class='filename' >$name</div>
	</a>
</div>$overlay_js

SCRIPT
				);
				
				// COUNT NEXT FILE
				$ctr++;
				
				// STOP ITERATING IF ONLY ONE FILE NEEDS TO BE DISPLAYED
				if ( !$display_all ) break;
			}
			else if ( $display_all )
			{
				// REMOVE FILE EXTENSION
				$name = str_ireplace ( '.' . $zfextension, '', $file );
				
				// SPLIT THE RAW FILENAME TO MAKE IT FIT THE THUMBNAIL
				$name = TASK::PX_PER_WORD ( $name, $thumbsize, $fontsize );
				
				// BUILD THE HTML SCRIPT FOR THE HTML ELEMENT
				$result .= str_replace ( "\r\n", "\r\n" . TASK::STRING_OF ( "\t", $tabs ),
<<<SCRIPT

<div class = 'container' >
	<a $property >
		<div class='icon'>
			<img id='zip$ctr$unique' class='icon' />
		</div>
		<div class='filename' >$name</div>
	</a>
</div>
<script>$('img#zip$ctr$unique.icon').attr('src', any);</script>

SCRIPT
				);
				
				// COUNT NEXT FILE
				$ctr++;
			}
		}
		
		// IF ZIP ARCHIVE DOES NOT CONTAIN ANY FILE (NOTHING TO BE DISPLAYED)
		if ( $ctr == 0 && !$display_all )
		{
			// CREATE A UNIQUE HASH FOR THE HTML ELEMENT
			$unique = md5 ( time() . rand ( 0,9999 ) );
			
			// IF A ZIP FILE IS OPENED
			// INSERT HTML IMAGE OVERLAY FOR ZIP ICONS
			if ( !$zipOpened )
			{
				// CREATE AN OVERLAY ELEMENT
				$overlay_ht =	"\r\n\t\t\t<div id='zip$ctr$unique' class='zip-overlay'>".
								"<img id='zip$ctr$unique' class='zip-overlay' />".
								"</div>";
								
				// PREPARE THE JAVASCRIPT FOR CONTROLLING THE OVERLAY FADE IN/OUT
				$overlay_js =	"\r\n<script>$('img#zip$ctr$unique.zip-overlay').attr('src', zip);</script>".
								"\r\n<script>zip_overlay_control('#zip$ctr$unique.zip-overlay');</script>";
			}
			
			// BUILD THE HTML SCRIPT FOR THE HTML ELEMENT
			$result .= str_replace ( "\r\n", "\r\n" . TASK::STRING_OF ( "\t", $tabs ),
<<<SCRIPT

<div class = 'container' >
	<a href='?$parent_varname=$file_url$parent_url_extras' class='ajax' >
		<div class='icon'>
			<img id='zip$ctr$unique' class='icon' />$overlay_ht
		</div>
		<div class='filename' >$name</div>
	</a>
</div>
<script>$('img#zip$ctr$unique.icon').attr('src', any);</script>$overlay_js

SCRIPT
			);
		}
		
		if ( $echo_result )
		{
			echo $result;
			die;
		}
		
		return $result;
	}
	
}


CLASS SCRIPTS
{
	/*///------------------------------------------------------------------------
			>>> LOAD IMAGES FROM BASE64 ENCODED RESOURCE
	/*///------------------------------------------------------------------------
	FINAL PUBLIC STATIC FUNCTION IMAGE
	(
			string $HTMLElements,		// HTML ELEMENTS TO CONTROL
			array $data = [],			// DATA TO BE APPLIED ON ELEMENT
			string $datasources = '',	// RAW SOURCES FOR DATA
			int $tabs = 0,				// NUMBER OF TABS / INDENTATION
			bool $allow404 = true		// ALLOW 404 ERROR MESSAGE?
	)
	{
		// ITERATE THROUGH COMMA SEPARATED DATA SOURCES
		foreach ( TASK::DISASSEMBLE ( $datasources ) as $source )
		{
			// IF THE SOURCE IS AN EXISTING FILE
			if ( file_exists ( $source ) )
			{
				// FILE ENCODE:
				// APPLY JAVASCRIPT-FRIENDLY BASE64 ENCODING TO FILE DATA
				$data[] = TASK::FENCODE ( $source, 1 );
			}
			// IF THE SOURCE IS A RESOURCE REFERENCE
			else if ( defined ( "FILES::{$source}" ) )
			{
				// RESOURCE ENCODE:
				// APPLY JAVASCRIPT-FRIENDLY BASE64 ENCODING TO BASE64 ENCODED RESOURCE
				$data[] = TASK::RENCODE ( $source, 1 );
			}
			// IF 404 NOT FOUND IMAGE IS ALLOWED
			else if ( $allow404 )
			{
				// GET ERROR404 IMAGE FROM RESOURCE
				$data[] = TASK::RENCODE ( 'error404', 1 );
