function not_fullscreen()
{
	return (( document.fullScreenElement !== undefined && document.fullScreenElement === null ) ||
			( document.msFullscreenElement !== undefined && document.msFullscreenElement === null ) ||
			( document.mozFullScreen !== undefined && !document.mozFullScreen ) ||
			( document.webkitIsFullScreen !== undefined && !document.webkitIsFullScreen ));
}

// TOGGLE VIDEO FULLSCREEN
function toggle_fullscreen ( element )
{
	var result = false;
	if ( not_fullscreen() )
	{
		if (element.requestFullscreen)
		{
			element.requestFullscreen();
			result = true;
		}
		else if (element.mozRequestFullScreen)
		{
			// Firefox
			element.mozRequestFullScreen();
			result = true;
		}
		else if (element.webkitRequestFullscreen)
		{
			// Chrome and Safari
			element.webkitRequestFullscreen(element.ALLOW_KEYBOARD_INPUT);
			result = true;
		}
		else if (element.msRequestFullscreen)
		{
			// IE
			element.msRequestFullscreen();
			result = true;
		}
	}
	else
	{
		if (document.cancelFullScreen)
		{
			document.cancelFullScreen();
		}
		else if (document.mozCancelFullScreen)
		{
			document.mozCancelFullScreen();
		}
		else if (document.webkitCancelFullScreen)
		{
			document.webkitCancelFullScreen();
		}
		else if (document.msExitFullscreen)
		{
			document.msExitFullscreen();
		}
	}
	return result;
}

// CONTROL CUSTOM VIDEO CONTROLS
function video_controls(identifier)
{
	// SET DEFAULT
	identifier = typeof identifier !== 'undefined' ? identifier : '';
	
	// VIDEO SCREEN MODE CONTAINER
	var vidContainer = document.getElementById("video-screen-mode-container" + identifier);
	
	// VIDEO
	var vid = document.getElementById("video" + identifier);
	
	// BUTTONS
	var playButton = document.getElementById("play-pause" + identifier);
	var muteButton = document.getElementById("mute" + identifier);
	var fullScreenButton = document.getElementById("full-screen" + identifier);
	
	// SLIDERS
	var seekBar = document.getElementById("seek-bar" + identifier);
	var volumeBar = document.getElementById("volume-bar" + identifier);
	
	// CONTROL GROUP
	var controlGroup = $("#video-controls" + identifier);
	var controlGroupContainer = $("#video-control-container" + identifier);
	
	// TIMEOUT FOR VIDEO CONTROLS
	var timeout = null;
	
	// PLAY STATUS
	var videoIsPlaying = false;
	
	// FULL-SCREEN STATUS
	var isFullScreen = false;
	
	// SET INITIAL VOLUME
	vid.volume = volumeBar.value;
	
	// SET START TIME
	vid.currentTime = seekBar.value * vid.duration;
	
	// DISABLE VIDEO CONTROLS
	vid.controls = false;
	
	// CONTROLS HEIGHT
	var controlDisplay = controlGroupContainer.css('display');
	//var controlHeight = controlGroupContainer.height();
	
	// CHANGE OPACITY OF CONTROLS
	controlGroup.css('opacity', '0.6');
	
	setInterval ( function() {
		if ( vid.controls == true )
		{
			controlGroupContainer.css('display', 'none');
			//controlGroupContainer.css('visibility', 'hidden');
			//controlGroupContainer.height(0);
		}
		else
		{
			controlGroupContainer.css('display', controlDisplay);
			//controlGroupContainer.css('visibility', 'visible');
			//controlGroupContainer.height(controlHeight);
		}
		isFullScreen = !not_fullscreen();
	}, 250 );
	
	setTimeout(function() {
			controlGroup.fadeOut();
	}, 1000);
	
	$(document).on('mousemove', function() {
		clearTimeout(timeout);
		
		timeout = setTimeout(function() {
			controlGroup.fadeOut();
		}, 3000);
	});
	
	controlGroupContainer.mouseenter(function(){
		controlGroup.fadeIn();
	});
	
	controlGroupContainer.mouseleave(function(){
		if ( isFullScreen ) controlGroup.fadeOut();
	});
	
	vidContainer.addEventListener("mousemove", function(){
		if ( !isFullScreen ) controlGroup.fadeIn();
	});
	
	vidContainer.addEventListener("mouseleave", function(){
		if ( !isFullScreen ) controlGroup.fadeOut();
	});
	
	vidContainer.addEventListener("mouseenter", function(){
		if ( !isFullScreen ) controlGroup.fadeIn();
	});
	
	// EVENT LISTENER FOR THE PLAY/PAUSE BUTTON
	vid.addEventListener("click", function() {
		if ( controlGroup.is(":visible") )
		{
			if (vid.paused == true)
			{
				// PLAY THE VIDEO
				vid.play();
				
				// UPDATE THE BUTTON TEXT TO 'PAUSE'
				playButton.innerHTML = "||";
				
				// UPDATE THE PLAY STATUS
				videoIsPlaying = true;
				
				timeout = setTimeout(function() {
					controlGroup.fadeOut();
				}, 3000);
			}
			else
			{
				// PAUSE THE VIDEO
				vid.pause();
				
				// UPDATE THE BUTTON TEXT TO 'PLAY'
				playButton.innerHTML = "&#9654;";
				
				// UPDATE THE PLAY STATUS
				videoIsPlaying = false;
				
				timeout = setTimeout(function() {
					controlGroup.fadeOut();
				}, 3000);
			}
		}
		else
		{
			controlGroup.fadeIn();
		}
	});
	
	// EVENT LISTENER FOR THE FULL-SCREEN BUTTON
	vid.addEventListener("dblclick", function() {
		isFullScreen = toggle_fullscreen ( vidContainer );
	});
	
	// EVENT LISTENER FOR THE FULL-SCREEN BUTTON
	fullScreenButton.addEventListener("click", function() {
		isFullScreen = toggle_fullscreen ( vidContainer );
	});
	
	// EVENT LISTENER FOR THE PLAY/PAUSE BUTTON
	playButton.addEventListener("click", function() {
		if ( vid.paused )
		{
			// PLAY THE VIDEO
			vid.play();
			
			// UPDATE THE BUTTON TEXT TO 'PAUSE'
			playButton.innerHTML = "||";
			
			// UPDATE THE PLAY STATUS
			videoIsPlaying = true;
		}
		else
		{
			// PAUSE THE VIDEO
			vid.pause();
			
			// UPDATE THE BUTTON TEXT TO 'PLAY'
			playButton.innerHTML = "&#9654;";
			
			// UPDATE THE PLAY STATUS
			videoIsPlaying = false;
		}
	});
	
	// EVENT LISTENER FOR THE MUTE BUTTON
	muteButton.addEventListener("click", function() {
		if (vid.muted == false)
		{
			// MUTE THE VIDEO
			vid.muted = true;
			
			// UPDATE THE BUTTON TEXT
			muteButton.innerHTML = "&#128263;";
		}
		else
		{
			// UNMUTE THE VIDEO
			vid.muted = false;
			
			// UPDATE THE BUTTON TEXT
			muteButton.innerHTML = "&#128264;";
		}
	});
	
	// EVENT LISTENER FOR THE SEEK BAR
	seekBar.addEventListener("change", function() {
		if ( !videoIsPlaying ) vid.pause();
		
		// UPDATE THE VIDEO TIME
		vid.currentTime = seekBar.value * vid.duration;
		
		if ( videoIsPlaying ) vid.play();
	});
	
	// UPDATE THE SEEK BAR AS THE VIDEO PLAYS
	vid.addEventListener("timeupdate", function() {
		if ( !vid.paused && videoIsPlaying && seekBar.value !== 1 )
		{
			// UPDATE THE SLIDER VALUE
			seekBar.value = vid.currentTime / vid.duration;
		}
	});
	
	vid.addEventListener("ended", function() {
		// PAUSE THE VIDEO
		vid.pause();
		
		// UPDATE THE BUTTON TEXT TO 'PLAY'
		playButton.innerHTML = "&#9654;";
		
		// UPDATE THE PLAY STATUS
		videoIsPlaying = false;
		
		// UPDATE SEEKBAR
		seekBar.value = 1;
	});
	
	// PAUSE THE VIDEO WHEN THE SLIDER HANDLE IS BEING DRAGGED
	seekBar.addEventListener("mousedown", function() {
		vid.pause();
		vid.currentTime = seekBar.value * vid.duration;
	});
	
	// PLAY THE VIDEO WHEN THE SLIDER HANDLE IS DROPPED
	seekBar.addEventListener("mouseup", function() {
		if ( videoIsPlaying ) vid.play();
	});
	
	// EVENT LISTENER FOR THE VOLUME BAR
	volumeBar.addEventListener("change", function() {
		// UPDATE THE VIDEO VOLUME
		vid.volume = volumeBar.value;
	});
}

